import React from "react";

export default function TransactionForm({
  typeOption, setTypeOption,
  categoryOption, setCategoryOption,
  amountValue, setAmountValue,
  dateValue, setDateValue,
  descriptionValue, setDescriptionValue,
  handleTransactionAdd,
  handleTransactionEdit,
  error,
  success,
  categories,
  spendingType,
}) {
  return (
    <div style={{ width: "300px", backgroundColor: "#003366", padding: "20px", color: "white", display: "flex", flexDirection: "column", alignItems: "center" }}>
      <h2>ENTER A TRANSACTION</h2>

      {/* Transaction Type */}
      <select
        value={typeOption}
        onChange={(e) => {
          setTypeOption(e.target.value);
          setCategoryOption("");
        }}
        style={{ padding: "10px", fontSize: "16px", borderRadius: "6px", border: "none", width: "100%", marginBottom: "20px" }}
      >
        <option value="">Transaction Type</option>
        <option value="income">Income</option>
        <option value="expense">Expense</option>
        <option value="savings">Savings</option>
      </select>

      {/* Category */}
      <select
        value={categoryOption}
        onChange={(e) => setCategoryOption(e.target.value)}
        disabled={!typeOption}
        style={{ padding: "10px", fontSize: "16px", borderRadius: "6px", border: "none", width: "100%", marginBottom: "20px" }}
      >
        <option value="">Category</option>
        {typeOption && categories[typeOption].map((cat, i) => (
          <option key={i} value={cat}>{cat}</option>
        ))}
      </select>

      {/* Amount */}
      <input
        type="text"
        placeholder="Amount"
        value={amountValue}
        onChange={(e) => setAmountValue(e.target.value)}
        style={{ padding: "10px", fontSize: "16px", borderRadius: "6px", border: "none", width: "94%", marginBottom: "20px" }}
      />

      {/* Date */}
      <input
        type="text"
        placeholder="Date (DD/MM/YYYY)"
        value={dateValue}
        onChange={(e) => setDateValue(e.target.value)}
        style={{ padding: "10px", fontSize: "16px", borderRadius: "6px", border: "none", width: "94%", marginBottom: "20px" }}
      />

      {/* Description */}
      <textarea
        placeholder="Description (optional)"
        value={descriptionValue}
        onChange={(e) => setDescriptionValue(e.target.value)}
        rows={5}
        style={{ padding: "10px", fontSize: "16px", borderRadius: "6px", border: "none", width: "94%", marginBottom: "20px", resize: "vertical", flex: 1 }}
      />

      {/* Buttons */}
      <div style={{ display: "flex", gap: "10px", marginTop: "0px" }}>
        <button onClick={handleTransactionAdd} style={{ flex: 1, padding: "12px 24px" }}>Add</button>
        <button onClick={handleTransactionEdit} style={{ flex: 1, padding: "12px 24px" }}>Edit</button>
      </div>

      {/* Error / Success */}
      {error && <p style={{ color: "red", fontWeight: "bold" }}>{error}</p>}
      {success && <p style={{ color: "green", fontWeight: "bold" }}>{success}</p>}
    </div>
  );
}
