import React, { useEffect, useState } from "react";

function Transactions() {

  const [transactions, setTransactions] = useState([]);
  const [error, setError] = useState("");

  // Load transactions when page opens
  useEffect(() => {
    fetchTransactions();
  }, []);

  const fetchTransactions = async () => {
    try {
      const response = await fetch("http://localhost:5000/api/transactions", {
        credentials: "include" // important if using sessions
      });

      const data = await response.json();

      if (!response.ok) {
        setError(data.error || "Failed to load transactions");
        return;
      }

      setTransactions(data);
    } catch (err) {
      console.error(err);
      setError("Network error");
    }
  };

  // Delete transaction
  const deleteTransaction = async (id) => {
    if (!window.confirm("Delete this transaction?")) return;

    await fetch(`http://localhost:5000/api/transactions/${id}`, {
      method: "DELETE",
      credentials: "include"
    });

    fetchTransactions(); // reload table
  };

  return (
    <div style={{
      backgroundColor: "#dbeeff",
      border: "5px solid #003366",
      minHeight: "100vh",
      padding: "40px",
      fontFamily: "Arial"
    }}>

      <h1 style={{ textAlign: "center" }}>TRANSACTIONS</h1>

      {error && (
        <p style={{ color: "red", textAlign: "center" }}>{error}</p>
      )}

      <table style={{
        width: "100%",
        borderCollapse: "collapse",
        marginTop: "30px"
      }}>
        <thead>
          <tr style={{ backgroundColor: "#003366", color: "white" }}>
            <th>Type</th>
            <th>Category</th>
            <th>Spending Type</th>
            <th>Amount</th>
            <th>Date</th>
            <th>Description</th>
            <th>Actions</th>
          </tr>
        </thead>

        <tbody>
          {transactions.length === 0 && (
            <tr>
              <td colSpan="7" style={{ textAlign: "center", padding: "20px" }}>
                No transactions found
              </td>
            </tr>
          )}

          {transactions.map((t) => (
            <tr key={t.id}>

              <td>{t.type}</td>
              <td>{t.category}</td>
              <td>{t.spendingType}</td>
              <td>${t.amount.toFixed(2)}</td>
              <td>{t.date}</td>
              <td>{t.description}</td>

              <td>
                <button
                  style={buttonStyle}
                  onClick={() => alert("Edit coming soon")}
                >
                  Edit
                </button>

                <button
                  style={{ ...buttonStyle, backgroundColor: "darkred" }}
                  onClick={() => deleteTransaction(t.id)}
                >
                  Delete
                </button>
              </td>

            </tr>
          ))}
        </tbody>
      </table>

    </div>
  );
}

const buttonStyle = {
  padding: "6px 10px",
  margin: "3px",
  backgroundColor: "#003366",
  color: "white",
  border: "none",
  borderRadius: "4px",
  cursor: "pointer"
};

export default Transactions;
