import React, { useEffect, useState } from "react";
import { useNavigate, Link } from "react-router-dom";

function Transactions() {
    const navigate = useNavigate();

    const [transactions, setTransactions] = useState([]);
    const [typeSortDir, setTypeSortDir] = useState("asc");
    const [error, setError] = useState("");

    // Loads transactiosn when the page opens
    useEffect(() => {
        fetchTransactions();
    }, []);

    // Fetching transactions
    const fetchTransactions = async () => {
        try {
        const response = await fetch("http://localhost:5000/api/transactions", {
            credentials: "include"
        });

        const data = await response.json();

        if (!response.ok) {
            setError(data.error || "Failed to load transactions");
            return;
        }

        setTransactions(data);
        } catch (err) {
        console.error(err);
        setError("Network error");
        }
    };

    // Deleting transaction
    const deleteTransaction = async (id) => {
        if (!window.confirm("Delete this transaction?")) return;

        await fetch(`http://localhost:5000/api/transactions/${id}`, {
        method: "DELETE",
        credentials: "include"
        });

        fetchTransactions(); // reloading the table
    };

    // Returns to previous page
    const goBack = async () => {
        navigate("/planner")
    }

    // Sorting by Type
    const sortType = () => {
        const direction = typeSortDir;
        const sorted = [...transactions].sort((a, b) => {
        if (a.type < b.type) return direction === "asc" ? -1 : 1;
        if (a.type > b.type) return direction === "asc" ? 1 : -1;
        return 0;
        });

        setTransactions(sorted);
        setTypeSortDir(direction === "asc" ? "desc" : "asc");
    }

    return (
    <div style={{
      backgroundColor: "#dbeeff",
      border: "5px solid #003366",
      minHeight: "100vh",
      padding: "40px",
      fontFamily: "Arial"
    }}>
        
        {/* Title Container */}
        <div style={{
            display: "flex",
            alignItems: "center"
        }}>
            <button style={{
                ...buttonStyle,
                padding: "12px, 20px",
                fontSize: "23px"}}
                 onClick={goBack}
            >
                Back
            </button>

            <h1 style={{ flex: 1, textAlign: "center" }}>
                TRANSACTIONS
            </h1>
        </div>
        
        {/* Error text */}
        {error && (
            <p style={{ color: "red", textAlign: "center" }}>{error}</p>
        )}

        {/* Transaction Table */}
        <table style={{
            width: "100%",
            borderCollapse: "collapse",
            marginTop: "30px",
            textAlign: "center"
        }}>
            {/* Column Headers */}
            <thead>
            <tr style={{ backgroundColor: "#003366", color: "white" }}>
                <th style={cellStyle}>
                    Type{" "}
                    <button
                        style={sortButtonStyle}
                        onClick={() => sortType()}
                    >
                        ⇅
                    </button>
                </th>

                <th style={cellStyle}>
                    Category{" "}
                    <button
                        style={sortButtonStyle}
                        onClick={() => console.log("Sort Category clicked")}
                    >
                        ⇅
                    </button>
                </th>

                <th style={cellStyle}>
                    Spending Type{" "}
                    <button
                        style={sortButtonStyle}
                        onClick={() => console.log("Sort SpendingType clicked")}
                    >
                        ⇅
                    </button>
                </th>

                <th style={cellStyle}>
                    Amount{" "}
                    <button
                        style={sortButtonStyle}
                        onClick={() => console.log("Sort Amount clicked")}
                    >
                        ⇅
                    </button>
                </th>

                <th style={cellStyle}>
                    Date{" "}
                    <button
                        style={sortButtonStyle}
                        onClick={() => console.log("Sort Date clicked")}
                    >
                        ⇅
                    </button>
                </th>

                <th style={cellStyle}>Description</th>
                <th style={cellStyle}>Actions</th>
            </tr>
            </thead>

            {/* Table Body */}
            <tbody>
            {transactions.length === 0 && (
                <tr>
                <td colSpan="7" style={{ textAlign: "center", padding: "20px" }}>
                    No transactions found
                </td>
                </tr>
            )}

            {transactions.map((t) => (
                <tr key={t.id}>

                <td style={cellStyle}>{t.type}</td>
                <td style={cellStyle}>{t.category}</td>
                <td style={cellStyle}>{t.spendingType}</td>
                <td style={{
                    ...cellStyle,
                    color: t.type === "expense" ? "red" : "green",
                    fontWeight: "bold"
                }}>
                    {t.type === "expense" ? "-" : "+"}${t.amount.toFixed(2)}
                </td>
                <td style={cellStyle}>{t.date}</td>
                <td style={cellStyle}>{t.description}</td>

                <td style={cellStyle}>
                    <button
                    style={buttonStyle}
                    onClick={() => alert("Edit coming soon")}
                    >
                    Edit
                    </button>

                    <button
                    style={{ ...buttonStyle, backgroundColor: "darkred" }}
                    onClick={() => deleteTransaction(t.id)}
                    >
                    Delete
                    </button>
                </td>

                </tr>
            ))}
            </tbody>
        </table>

    </div>
  );
}

const buttonStyle = {
  padding: "6px 10px",
  margin: "3px",
  backgroundColor: "#003366",
  color: "white",
  border: "none",
  borderRadius: "4px",
  cursor: "pointer"
};

const cellStyle = {
  border: "2px solid black",
  padding: "8px"
};

const sortButtonStyle = {
  marginLeft: "5px",
  padding: "2px 5px",
  fontSize: "12px",
  cursor: "pointer",
  borderRadius: "4px",
  border: "1px solid #ccc",
  backgroundColor: "#f0f0f0"
};


export default Transactions;
