// Pie charts
const PieGraphCard = ({ title, data }) => (
  <div style={{
    flex: 1,
    padding: "16px",
    backgroundColor: "#f9fafb",
    borderRadius: "10px",
    boxShadow: "0 2px 4px rgba(0,0,0,0.08)",
    display: "flex",
    flexDirection: "column"
  }}>
    <h4>{title}</h4>

    <div style={{ flex: 1 }}>
      <ResponsiveContainer width="100%" height="100%">
        <PieChart>
          <Pie
            data={data}
            dataKey="value"
            nameKey="name"
            outerRadius={90}
            label
          >
            {data.map((_, index) => (
              <Cell
                key={index}
                fill={["#3b82f6", "#22c55e", "#f59e0b", "#a855f7"][index % 4]}
              />
            ))}
          </Pie>
          <Legend />
        </PieChart>
      </ResponsiveContainer>
    </div>
  </div>
);

// Card for line graphs
const GraphCard = ({ title, data }) => (
  <div style={{
    flex: 1,
    padding: "16px",
    backgroundColor: "#f9fafb",
    borderRadius: "10px",
    boxShadow: "0 2px 4px rgba(0,0,0,0.08)",
    display: "flex",
    flexDirection: "column"
  }}>
    <h4>{title}</h4>
    <div style={{ flex: 1 }}>
      <ResponsiveContainer width="100%" height="100%">
        <LineChart data={data}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="month" />
          <YAxis />
          <Tooltip />
          <Line
            type="monotone"
            dataKey="amount"
            stroke="#3b82f6"
            strokeWidth={2}
          />
        </LineChart>
      </ResponsiveContainer>
    </div>
  </div>
);

