import React from "react";
import ViewSelector from "./ViewSelector";

export default function TransactionForm({
  typeOption, setTypeOption,
  categoryOption, setCategoryOption,
  amountValue, setAmountValue,
  dateValue, setDateValue,
  descriptionValue, setDescriptionValue,
  handleTransactionAdd,
  handleTransactionEdit,
  error,
  success,
  categories,
  dateRangeOption,
  setDateRangeOption,
  monthValue,
  setMonthValue,
  yearValue,
  setYearValue,
  prevYearValue,
  setPrevYearValue,
  currentView,
  handleViewChange
}) {
  return (
    <div style={{ width: "300px", backgroundColor: "#003366", padding: "20px", color: "white", display: "flex", flexDirection: "column", alignItems: "center" }}>
      <h2>ENTER A TRANSACTION</h2>

      {/* Transaction Type */}
      <select value={typeOption} onChange={e => { setTypeOption(e.target.value); setCategoryOption(""); }} style={{ padding: "10px", fontSize: "16px", borderRadius: "6px", border: "none", width: "100%", marginBottom: "20px" }}>
        <option value="">Transaction Type</option>
        <option value="income">Income</option>
        <option value="expense">Expense</option>
        <option value="savings">Savings</option>
      </select>

      {/* Category */}
      <select value={categoryOption} onChange={e => setCategoryOption(e.target.value)} disabled={!typeOption} style={{ padding: "10px", fontSize: "16px", borderRadius: "6px", border: "none", width: "100%", marginBottom: "20px" }}>
        <option value="">Category</option>
        {typeOption && categories[typeOption].map((cat, i) => <option key={i} value={cat}>{cat}</option>)}
      </select>

      {/* Amount */}
      <input type="text" placeholder="Amount" value={amountValue} onChange={e => setAmountValue(e.target.value)} style={{ padding: "10px", fontSize: "16px", borderRadius: "6px", border: "none", width: "94%", marginBottom: "20px" }} />

      {/* Date */}
      <input type="text" placeholder="Date (DD/MM/YYYY)" value={dateValue} onChange={e => setDateValue(e.target.value)} style={{ padding: "10px", fontSize: "16px", borderRadius: "6px", border: "none", width: "94%", marginBottom: "20px" }} />

      {/* Description */}
      <textarea placeholder="Description (optional)" value={descriptionValue} onChange={e => setDescriptionValue(e.target.value)} rows={5} style={{ padding: "10px", fontSize: "16px", borderRadius: "6px", border: "none", width: "94%", marginBottom: "20px", resize: "vertical", flex: 1 }} />

      {/* Buttons */}
      <div style={{ display: "flex", gap: "10px", marginTop: "0px" }}>
        <button onClick={handleTransactionAdd} style={{ padding: "12px 24px", flex: 1 }}>Add</button>
        <button onClick={handleTransactionEdit} style={{ padding: "12px 24px", flex: 1 }}>Edit</button>
      </div>

      {/* Error / Success */}
      {error && <p style={{ color: "red", marginTop: "10px", fontWeight: "bold" }}>{error}</p>}
      {success && <p style={{ color: "green", marginTop: "10px", fontWeight: "bold" }}>{success}</p>}

      {/* Date Range Selector */}
      <div style={{ display: "flex", marginTop: "40px", marginBottom: "10px" }}>
        {["monthly", "yearly", ""].map(range => (
          <button key={range} onClick={() => setDateRangeOption(range)}
            style={{ padding: "12px 24px", backgroundColor: dateRangeOption === range ? "#5a8fcf" : "#dbeeff", color: "black", fontSize: "16px", cursor: "pointer", flex: 1 }}>
            {range === "monthly" ? "Monthly" : range === "yearly" ? "Yearly" : "Overall"}
          </button>
        ))}
      </div>

      {/* Month / Year Select */}
      <div style={{ display: "flex", gap: "12px", width: "240px" }}>
        <select value={monthValue} onChange={e => setMonthValue(e.target.value)} disabled={dateRangeOption !== "monthly"} style={{ padding: "10px", fontSize: "16px", borderRadius: "6px", border: "none", width: "54%" }}>
          {Array.from({ length: 12 }, (_, i) => {
            const val = (i + 1).toString().padStart(2, "0");
            return <option key={val} value={val}>{new Date(0, i).toLocaleString("default", { month: "long" })}</option>;
          })}
        </select>

        <input type="number" value={yearValue} min={1976} max={2076} onChange={e => setYearValue(e.target.value)} disabled={dateRangeOption === ""} onBlur={() => {
          const numericValue = Number(yearValue);
          if (numericValue < 1976 || numericValue > 2076 || isNaN(numericValue)) {
            setYearValue(prevYearValue);
          } else {
            setPrevYearValue(numericValue);
          }
        }} style={{ padding: "10px", fontSize: "16px", borderRadius: "6px", border: "none", width: "46%" }} />
      </div>

      {/* View Selector */}
      <ViewSelector currentView={currentView} handleViewChange={handleViewChange} />
    </div>
  );
}
