import React, { useState, useEffect } from "react";
import { useNavigate, Link } from "react-router-dom";
import {
  LineChart,
  BarChart,
  Bar,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
  PieChart,
  Pie,
  Cell,
  Legend
} from "recharts";

function Planner() {
  const navigate = useNavigate();

  // States
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [typeOption, setTypeOption] = useState("");
  const [categoryOption, setCategoryOption] = useState("");
  const [amountValue, setAmountValue] = useState("");
  const [dateValue, setDateValue] = useState("");
  const [descriptionValue, setDescriptionValue] = useState("");

  // View Date Range
  const [yearValue, setYearValue] = useState("2026");
  const [prevYearValue, setPrevYearValue] = useState("2026");
  const [monthValue, setMonthValue] = useState("01");
  const [dateRangeOption, setDateRangeOption] = useState("monthly");

  // View States
  const [currentView, setCurrentView] = useState("General");

  // General view states
  const [totalIncome, setTotalIncome] = useState(0);
  const [periodTransactions, setPeriodTransactions] = useState([]);
  const [totalExpenses, setTotalExpenses] = useState(0);
  const [periodCashflow, setPeriodCashflow] = useState([]);
  const [totalSavings, setTotalSavings] = useState(0);
  const [savingsRate, setSavingsRate] = useState(0);
  const [EIRatio, setEIRatio] = useState(0);
  const [emergencyFund, setEmergencyFund] = useState(0);
  const [runway, setRunway] = useState(0);

  // Income view states
  const [prevIncome, setPrevIncome] = useState(0);
  const [incomeChange, setIncomeChange] = useState(0);
  const [incomeChangePercent, setIncomeChangePercent] = useState(0);
  const [largestIncomeTransactions, setLargestIncomeTransactions] = useState([]);
  const [periodIncome, setPeriodIncome] = useState([]);
  const [categoryIncome, setCategoryIncome] = useState([]);

  // Expense view states
  const [prevExpenses, setPrevExpenses] = useState(0);
  const [expenseChange, setExpenseChange] = useState(0);
  const [expenseChangePercent, setExpenseChangePercent] = useState(0);
  const [largestExpenseTransactions, setLargestExpenseTransactions] = useState([]);
  const [periodExpenses, setPeriodExpenses] = useState([]);
  const [categoryExpenses, setCategoryExpenses] = useState([]);
  const [categorySpendingType, setCategorySpendingType] = useState([]);

  // Trends view states
  const [changingExpenses, setChangingExpenses] = useState([]);
  const [changingIncome, setChangingIncome] = useState([]);
  const [categoryGrowth, setCategoryGrowth] = useState([]);

  // Categories mapped by type
  const categories = {
    income: ["Salary", "Interest", "Investments", "Bonuses", "Other Income"],
    expense: [
      "Rent",
      "Utilities",
      "Insurance",
      "Health/Medical",
      "Education",
      "Travel",
      "Subscriptions",
      "Loan payment",
      "Leisure",
      "Clothes",
      "Food",
      "Other Expense"
    ],
    savings: ["Deposit", "Withdrawal"]
  };

  // Discretionary vs non discretionary mapping
  const spendingType = {
    discretionary: [
      "Travel",
      "Leisure",
      "Clothes",
      "Other Expense"
    ],
    nonDiscretionary: [
      "Rent",
      "Utilities",
      "Insurance",
      "Health/Medical",
      "Education",
      "Subscriptions",
      "Loan payment",
      "Savings",
      "Food"
    ]
  }

  const handleTransactionAdd = async () => {

    // Checking all fields are filled correctly

    // Transaction type is not blank
    if (!typeOption || !categoryOption || !amountValue || !dateValue) {
      setError("Required fields are empty");
      return;
    }

    // Amount is within bounds > 0 and < 1000000000 and if it has a $ or . it is removed and accounted for
    const amount = parseFloat(amountValue.replace(/[$,]/g, ""));
    if (isNaN(amount) || amount <= 0 || amount >= 1000000000) {
      setError("Invalid amount. Must be between $0 and $1 billion.");
      return;
    }

    // Date has correct format and is valid
    const dateRegex = /^(0?[1-9]|[12][0-9]|3[01])\/(0?[1-9]|1[0-2])\/\d{4}$/;
    if (!dateRegex.test(dateValue)) {
      setError("Invalid date.");
      return;
    }

    // Year is within reasonable range
    const year = parseInt(dateValue.split("/")[2], 10);

    if (year < 1976 || year > 2076) {
      setError("Year must be between 1976 and 2076.");
      return;
    }

    // Assigning spending type
    let spending = "NULL";
    if (spendingType.discretionary.includes(categoryOption)) {
      spending = "Discretionary";
    } else if (spendingType.nonDiscretionary.includes(categoryOption)) {
      spending = "Non-Discretionary";
    }

    // Storing the data
    try {
      const response = await fetch("http://localhost:5000/api/add", { 
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({ 
          type: typeOption, 
          category: categoryOption, 
          spendingType: spending,
          amount: amount, 
          date: dateValue, 
          description:descriptionValue 
        }),
      });

      const data = await response.json();

      if (!response.ok) {
        alert(data.message || "Adding transaction failed");
        return;
      }

      setSuccess("Added Transaction!");
      updateDateView();

      // Resetting states
      setTypeOption("");
      setCategoryOption("");
      setAmountValue("");
      setDateValue("");
      setDescriptionValue("");
    } catch (error) {
      console.error("Error adding transaction:", error);
      setError("Network error.");
    }
  };

  // Going to the transactions page
  const handleTransactionEdit = async () => {
    navigate("/transactions")
  }

  // Updating the date view from Monthly to Yearly and which date
  const updateDateView = async () => {
    let formattedDate = "";
    let oldDate = "";

    // Formatting the date
    if (dateRangeOption === "monthly") {
      formattedDate = `${yearValue}-${monthValue}`;

      // Reducing month by one
      let year = parseInt(yearValue, 10);
      let month = parseInt(monthValue, 10) - 1;
      if (month === 0) {
        month = 12;
        year -= 1;
      }

      oldDate = `${year}-${String(month).padStart(2, "0")}`
    } else if (dateRangeOption === "yearly") {
      formattedDate = `${yearValue}`;
      oldDate = `${yearValue - 1}`
    }

    fetchData(formattedDate, oldDate);
  }

  // Changing stat views
  const handleViewChange = async (view) => {
    setCurrentView(view);
  }

  // Function to fetch all relevant data
  const fetchData = (date, prevDate) => {
    fetchTotalIncome(date);
    fetchPrevPeriodIncome(prevDate);
    fetchTotalExpenses(date);
    fetchPrevPeriodExpenses(prevDate);
    fetchLargestExpenseChange(date, prevDate)
    fetchLargestIncomeChange(date, prevDate)
    fetchTotalSavings(date);
    fetchLargestIncome(date);
    fetchCategoryIncome(date);
    fetchCategoryExpenses(date);
    fetchSpendingType(date);
    fetchPeriodTransactions(date);
    fetchLargestExpense(date);
    fetchCategoryGrowth(date, prevDate);
  }

  // Fetch total income
  const fetchTotalIncome = (date) => {
    fetch(`http://localhost:5000/api/stats/total-income?date=${date}`, { credentials: "include" })
      .then(res => res.json())
      .then(data => setTotalIncome(data.total))
      .catch(err => console.error("Failed to fetch total income:", err));
  }

  // Fetches total income for the most recent period
  const fetchPrevPeriodIncome = (date) => {
    fetch(`http://localhost:5000/api/stats/total-income?date=${date}`, { credentials: "include" })
      .then(res => res.json())
      .then(data => setPrevIncome(data.total))
      .catch(err => console.error("Failed to fetch previous income:", err));
  }

  // Fetches income per category
  const fetchCategoryIncome = (date) => {
    fetch(`http://localhost:5000/api/stats/category-income?date=${date}`, { credentials: "include" })
      .then(res => res.json())
      .then(data => setCategoryIncome(data.incomeByCategory || []))
      .catch(err => console.error("Failed to fetch category income:", err));
  }

  // Fetch total expenses
  const fetchTotalExpenses = (date) => {
    fetch(`http://localhost:5000/api/stats/total-expenses?date=${date}`, { credentials: "include" })
      .then(res => res.json())
      .then(data => setTotalExpenses(data.total))
      .catch(err => console.error("Failed to fetch total expenses:", err));
  }

  // Fetch total expenses for the most recent period
  const fetchPrevPeriodExpenses = (date) => {
    fetch(`http://localhost:5000/api/stats/total-expenses?date=${date}`, { credentials: "include" })
      .then(res => res.json())
      .then(data => setPrevExpenses(data.total))
      .catch(err => console.error("Failed to fetch previous expenses:", err));
  }

  // Fetching the categories which changed the most
  const fetchLargestIncomeChange = (date, prevDate) => {
    fetch(`http://localhost:5000/api/stats/fastest-growing-income?date=${date}&prevDate=${prevDate}`, { credentials: "include" })
      .then(res => res.json())
      .then(data => setChangingIncome(data.fastestGrowingIncomes || []))
      .catch(err => console.error("Failed to fetch fastest growing income:", err));
  }

  // Fetching the categories which changed the most
  const fetchLargestExpenseChange = (date, prevDate) => {
    fetch(`http://localhost:5000/api/stats/fastest-growing-expenses?date=${date}&prevDate=${prevDate}`, { credentials: "include" })
      .then(res => res.json())
      .then(data => setChangingExpenses(data.fastestGrowingExpenses || []))
      .catch(err => console.error("Failed to fetch fastest growing expenses:", err));
  }

  // Fetch expenses per category
  const fetchCategoryExpenses = (date) => {
    fetch(`http://localhost:5000/api/stats/category-expenses?date=${date}`, { credentials: "include" })
      .then(res => res.json())
      .then(data => setCategoryExpenses(data.expensesByCategory || []))
      .catch(err => console.error("Failed to fetch category expenses:", err));
  }

  // Fetch expense spending type
  const fetchSpendingType = (date) => {
    fetch(`http://localhost:5000/api/stats/spending-type?date=${date}`, { credentials: "include" })
      .then(res => res.json())
      .then(data => setCategorySpendingType(data.expensesBySpendingType || []))
      .catch(err => console.error("Failed to fetch spending type:", err));
  }

  // Fetches transactions by period (e.g. day by day income and expenses for a month)
  const fetchPeriodTransactions = (date) => {
    fetch(`http://localhost:5000/api/stats/period-transactions?date=${date}`, { credentials: "include" })
      .then(res => res.json())
      .then(data => setPeriodTransactions(data.data || []))
      .catch(err => console.error("Failed to fetch period transactions:", err));
  }

  // Fetch total savings
  const fetchTotalSavings = (date) => {
    fetch(`http://localhost:5000/api/stats/total-savings?date=${date}`, { credentials: "include" })
      .then(res => res.json())
      .then(data => setTotalSavings(data.total))
      .catch(err => console.error("Failed to fetch total savings:", err));
  }

  // Fetch largest income transactions
  const fetchLargestIncome = (date) => {
    fetch(`http://localhost:5000/api/stats/largest-income?date=${date}`, { credentials: "include" })
      .then(res => res.json())
      .then(data => {
        const formatted = data.topIncome.map(tx => ({
          ...tx,
          amount: Number(tx.amount).toFixed(2)
        }));
        setLargestIncomeTransactions(formatted)
      })
      .catch(err => console.error("Failed to fetch largest income transactions:", err));
  }

  // Fetch the largest expense transactions
  const fetchLargestExpense = (date) => {
    fetch(`http://localhost:5000/api/stats/largest-expense?date=${date}`, { credentials: "include" })
      .then(res => res.json())
      .then(data => {
        const formatted = data.topExpense.map(tx => ({
          ...tx,
          amount: Number(tx.amount).toFixed(2)
        }));
        setLargestExpenseTransactions(formatted)
      })
      .catch(err => console.error("Failed to fetch largest expense transactions:", err));
  }

  // Fetching the growth in every category
  const fetchCategoryGrowth = (date, prevDate) => {
    fetch(`http://localhost:5000/api/stats/category-growth?date=${date}&prevDate=${prevDate}`, { credentials: "include" })
      .then(res => res.json())
      .then(data => setCategoryGrowth(data.changeInCategories || []))
      .catch(err => console.error("Failed to fetch change in categories:", err));
  }

  // Calculating other stats
  useEffect(() => {
    // Emergency Fund
    setEmergencyFund(((totalExpenses) * 6).toFixed(0));

    // Change in income
    setIncomeChange((totalIncome - prevIncome).toFixed(0));
    if (prevIncome != 0) {
      setIncomeChangePercent(((((totalIncome - prevIncome)/ prevIncome))*100).toFixed(0));
    } else {
      setIncomeChangePercent(0);
    }

    // Change in expenses
    setExpenseChange((totalExpenses - prevExpenses).toFixed(0));
    if (prevExpenses != 0) {
      setExpenseChangePercent(((((totalExpenses - prevExpenses)/ prevExpenses))*100).toFixed(0));
    } else {
      setExpenseChangePercent(0);
    }

    // Calculating cashflow over the period
    const cashFlow = periodTransactions.map(item => ({
      period: item.period,
      net: item.income - item.expense
    }));
    setPeriodCashflow(cashFlow);

    // Mapping income over the period
    const periodIncome = periodTransactions.map(item => ({
      period: item.period,
      income: item.income
    }))
    setPeriodIncome(periodIncome);

    // Mapping expenses over the period
    const periodExpenses = periodTransactions.map(item => ({
      period: item.period,
      expense: item.expense
    }))
    setPeriodExpenses(periodExpenses);

    if (totalIncome > 0 && totalExpenses > 0) {
      setSavingsRate((((totalIncome - totalExpenses) / totalIncome) * 100).toFixed(2));
      setEIRatio((totalExpenses / totalIncome).toFixed(2));
      setRunway((totalSavings / (totalExpenses)).toFixed(0))
    } else {
      setSavingsRate(0);
      setEIRatio(0);
      setRunway(0);
    }
  }, [
    totalIncome, prevIncome, categoryIncome, totalExpenses, prevExpenses, changingIncome, 
    changingExpenses, categoryExpenses, categorySpendingType, periodTransactions, totalSavings, 
    largestIncomeTransactions, largestExpenseTransactions, categoryGrowth
  ]);

  // One-time fetch on component mount
  useEffect(() => {
    let formattedDate = "";
    let oldDate = "";

    // Formatting the date
    if (dateRangeOption === "monthly") {
      formattedDate = `${yearValue}-${monthValue}`;


      // Reducing month by one
      let year = parseInt(yearValue, 10);
      let month = parseInt(monthValue, 10) - 1;
      if (month === 0) {
        month = 12;
        year -= 1;
      }

      oldDate = `${year}-${String(month).padStart(2, "0")}`
    } else if (dateRangeOption === "yearly") {
      formattedDate = `${yearValue}`;
      oldDate = `${yearValue - 1}`
    }

    fetchData(formattedDate, oldDate);
  }, []);

  // Updating stats after state changing
  useEffect(() => {
    updateDateView();
  }, [dateRangeOption, monthValue, yearValue]);

  return (
    <div>
      <style>
        {`
          body {
            margin: 0;
            padding: 0;
          }
        `}
      </style>

      {/* TOP TITLE RIBBON */}
      <div style={{
        position: "fixed",
        top: 0,
        left: 0,
        width: "100%",
        backgroundColor: "#bb4c0b",
        color: "white",
        padding: "18px",
        textAlign: "center",
        zIndex: 1000
      }}>
        <h1 style={{ margin: 0, letterSpacing: "2px" }}>
          FINANCIAL PLANNER
        </h1>
      </div>

      {/* Spacer so content is not hidden */}
      <div style={{ height: "60px" }} />

      <div style={{
        display: "flex",
        minHeight: "calc(100vh - 60px)",
        backgroundColor: "#dbeeff",
        fontFamily: "Arial, sans-serif"
      }}>

        {/* Left Ribbon */}
        <div style={{
          width: "300px",
          backgroundColor: "#003366",
          padding: "20px",
          color: "white",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
        }}>
          <h2>ENTER A TRANSACTION</h2>

          {/* Transaction Type Dropdown */}
          <select
            value={typeOption}
            onChange={(e) => {
              setTypeOption(e.target.value);
              setCategoryOption(""); // reset category when type changes
            }}
            onFocus={() => {
              setError("");
              setSuccess("");
            }}
            style={{
              padding: "10px",
              fontSize: "16px",
              borderRadius: "6px",
              border: "none",
              width: "100%",
              marginBottom: "20px"
            }}
          >
            <option value="">Transaction Type</option>
            <option value="income">Income</option>
            <option value="expense">Expense</option>
            <option value="savings">Savings</option>
          </select>

          {/* Category Dropdown */}
          <select
            value={categoryOption}
            onChange={(e) => setCategoryOption(e.target.value)}
            disabled={!typeOption} // disabled until type is selected
            onFocus={() => {
              setError("");
              setSuccess("");
            }}
            style={{
              padding: "10px",
              fontSize: "16px",
              borderRadius: "6px",
              border: "none",
              width: "100%",
              marginBottom: "20px"
            }}
          >
            <option value="">Category</option>
            {typeOption && categories[typeOption].map((cat, index) => (
              <option key={index} value={cat}>{cat}</option>
            ))}
          </select>

          {/* Amount */}
          <input
            type="text"
            placeholder="Amount"
            value={amountValue}
            onChange={(e) => setAmountValue(e.target.value)}
            onFocus={() => {
              setError("");
              setSuccess("");
            }}
            style={{
              padding: "10px",
              fontSize: "16px",
              borderRadius: "6px",
              border: "none",
              width: "94%",
              marginBottom: "20px"
            }}
          />

          {/* Date */}
          <input
            type="text"
            placeholder="Date (DD/MM/YYYY)"
            value={dateValue}
            onChange={(e) => setDateValue(e.target.value)}
            onFocus={() => {
              setError("");
              setSuccess("");
            }}
            style={{
              padding: "10px",
              fontSize: "16px",
              borderRadius: "6px",
              border: "none",
              width: "94%",
              marginBottom: "20px"
            }}
          />

          {/* Brief Description */}
          <textarea
            placeholder="Description (optional)"
            value={descriptionValue}
            onChange={(e) => setDescriptionValue(e.target.value)}
            onFocus={() => {
              setError("");
              setSuccess("");
            }}
            rows={5} // show 5 lines by default
            style={{
              padding: "10px",
              fontSize: "16px",
              borderRadius: "6px",
              border: "none",
              width: "94%",
              marginBottom: "20px",
              resize: "vertical",
              flex: 1
            }}
          />

          {/* Button Container */}
          <div style={{ display: "flex", gap: "10px", marginTop: "0px" }}>
            {/* ADD Button */}
            <button
              onFocus={() => {
                setError("");
                setSuccess("");
              }}
              onClick={handleTransactionAdd}
              style={{
                padding: "12px 24px",
                backgroundColor: "#dbeeff",
                color: "black",
                borderRadius: "6px",
                fontSize: "16px",
                cursor: "pointer",
                flex: 1
              }}
            >
              Add
            </button>

            {/* EDIT Button */}
            <button
              onFocus={() => {
                setError("");
                setSuccess("");
              }}
              onClick={handleTransactionEdit}
              style={{
                padding: "12px 24px",
                backgroundColor: "#dbeeff",
                color: "black",
                borderRadius: "6px",
                fontSize: "16px",
                cursor: "pointer",
                flex: 1 
              }}
            >
              Edit
            </button>
          </div>


          {/* Error Message */}
          {error && (
            <p style={{ color: "red", marginTop: "10px", fontWeight: "bold" }}>
              {error}
            </p>
          )}

          {/* Success Message */}
          {success && (
            <p style={{ color: "green", marginTop: "10px", fontWeight: "bold" }}>
              {success}
            </p>
          )}

           {/* View Range Selectors */}
          <div style={{ display: "flex", marginTop: "40px", marginBottom: "10px" }}>
            {/* Monthly Button */}
            <button
              onFocus={() => {
                setError("");
                setSuccess("");
              }}
              onClick={() => setDateRangeOption("monthly")}
              style={{
                padding: "12px 24px",
                backgroundColor: dateRangeOption === "monthly" ? "#5a8fcf" : "#dbeeff",
                color: "black",
                fontSize: "16px",
                cursor: "pointer",
                flex: 1
              }}
            >
              Monthly
            </button>

            {/* Yearly Button */}
            <button
              onFocus={() => {
                setError("");
                setSuccess("");
              }}
              onClick={() => setDateRangeOption("yearly")}
              style={{
                padding: "12px 24px",
                backgroundColor: dateRangeOption === "yearly" ? "#5a8fcf" : "#dbeeff",
                color: "black",
                fontSize: "16px",
                cursor: "pointer",
                flex: 1
              }}
            >
              Yearly
            </button>

            {/* Overall Button */}
            <button
              onFocus={() => {
                setError("");
                setSuccess("");
              }}
              onClick={() => setDateRangeOption("")}
              style={{
                padding: "12px 24px",
                backgroundColor: dateRangeOption === "" ? "#5a8fcf" : "#dbeeff",
                color: "black",
                fontSize: "16px",
                cursor: "pointer",
                flex: 1
              }}
            >
              Overall
            </button>
          </div>

          {/* Date Selection Dropdowns */}
          <div style={{ display: "flex", gap: "12px", width: "240px"}}>
            {/* Month Dropdown */}
            <select
              value={monthValue}
              onChange={(e) => setMonthValue(e.target.value)}
              disabled={dateRangeOption !== "monthly"} // disabled until type is selected
              onFocus={() => {
                setError("");
                setSuccess("");
              }}
              onBlur={() => updateDateView()}
              style={{
                padding: "10px",
                fontSize: "16px",
                borderRadius: "6px",
                border: "none",
                width: "54%"
              }}
            >
              <option value="01">January</option>
              <option value="02">February</option>
              <option value="03">March</option>
              <option value="04">April</option>
              <option value="05">May</option>
              <option value="06">June</option>
              <option value="07">July</option>
              <option value="08">August</option>
              <option value="09">September</option>
              <option value="10">October</option>
              <option value="11">November</option>
              <option value="12">December</option>
            </select>

            {/* Year Dropdown */}
            <input
              type="number"
              value={yearValue}
              min={1976}
              max={2076}
              onChange={(e) => setYearValue(e.target.value)}
              disabled={dateRangeOption === ""} 
              onFocus={() => {
                setError("");
                setSuccess("");
              }}
              onBlur={() => {
                const numericValue = Number(yearValue);
                if (numericValue < 1976 || numericValue > 2076 || isNaN(numericValue)) {
                  setYearValue(prevYearValue);
                } else {
                  setPrevYearValue(numericValue);
                }
                updateDateView();
              }}
              style={{
                padding: "10px",
                fontSize: "16px",
                borderRadius: "6px",
                border: "none",
                width: "46%"
              }}
            />
          </div>

          {/* View Buttons*/}
          <div style={{ display: "flex", flexDirection: "column", width: "100%", gap: "12px", marginTop: "50px", marginBottom: "20px"  }}>
            {["General","Income","Expense","Trends"].map((label) => (
              <button
                key={label}
                onClick={() => handleViewChange(label)}
                style={{
                  ...viewButtonStyle,
                  backgroundColor: currentView === label ? "#5a8fcf" : "#dbeeff"
                }}
              >
                {label}
              </button>
            ))}
          </div>
          </div>

          {/* Main Stats View */}
          <div style={{
            flex: 1,
            padding: "20px",
            backgroundColor: "#ffffff",
            borderRadius: "12px",
            boxShadow: "0 4px 8px rgba(0,0,0,0.1)",
            display: "flex",
            flexDirection: "column"
          }}>
            {currentView === "General" && (
              <div style={{ display: "flex", flexDirection: "column", gap: "16px", height: "100%" }}>

                {/* Main General Stats */}
                <div style={{ display: "flex", gap: "16px" }}>
                  <StatCard 
                    title="Total Savings" 
                    value = {`$${totalSavings.toLocaleString()}`} 
                    colour= { totalSavings>= 0 ? "green" : "red" } />
                  <StatCard title="Total Income" value = {`$${totalIncome.toLocaleString()}`} colour = "green" />
                  <StatCard title="Total Expenses" value = {`-$${totalExpenses.toLocaleString()}`} colour = "red" />
                  <StatCard 
                    title="Net Cashflow" 
                    value = {`$${(totalIncome - totalExpenses).toLocaleString()}`} 
                    colour= {totalIncome - totalExpenses >= 0 ? "green" : "red"}
                  />
                </div>

                {/* Graphs */}
                <div style={{ display: "flex", gap: "16px", flex: 1 }}>
                  <GraphCard 
                    title="Income vs Expenses" 
                    data = {periodTransactions} 
                    lines = {[
                      { dataKey: "income", color: "#22c55e" },
                      { dataKey: "expense", color: "#ef4444" }
                    ]}
                    dataKeyX = "period"
                  />
                  <GraphCard 
                    title="Net Cashflow" 
                    data = {periodCashflow} 
                    lines = {[
                      { dataKey: "net", color: "rgb(82, 116, 226)" }
                    ]}
                    dataKeyX = "period"
                  />
                </div>

                {/* Other General Stats */}
                <div style={{ display: "flex", gap: "16px" }}>
                  <StatCard 
                    title="Savings Rate" 
                    value = {`${savingsRate.toLocaleString()}%`} 
                    colour= {savingsRate >= 0 ? "green" : "red"}
                  />
                  <StatCard 
                    title="Expense / Income Ratio" 
                    value = {`${EIRatio.toLocaleString()} : 1`} 
                    colour = {EIRatio <= 0.8 ? "green" : "red"}
                  />
                  <StatCard 
                    title="Emergency Fund Estimate" 
                    value = {`$${emergencyFund.toLocaleString()}`}
                    colour = {emergencyFund <= totalSavings ? "green" : "red"}
                  />
                  <StatCard 
                    title="Months of Runway" 
                    value = {`${runway.toLocaleString()} months`}
                    colour = {runway >= 6 ? "green" : "red"}
                  />
                </div>

                {/* Forecast */}
                <div>
                  <ForecastCard title="Next Period Forecast" />
                </div>

              </div>
            )}
            {currentView === "Income" && (
              <div style={{ display: "flex", flexDirection: "column", gap: "16px", height: "100%" }}>

                {/* Major Income stats */}
                <div style={{ display: "flex", gap: "16px" }}>
                  <StatCard title="Total Income" value = {`$${totalIncome.toLocaleString()}`} colour = "green"/>
                  <StatCard 
                    title="Change in Income" 
                    value = {`$${incomeChange.toLocaleString()} (${incomeChangePercent.toLocaleString()}%)`} 
                    colour= {incomeChange >= 0 ? "green" : "red"}
                  />
                  <TransactionRankCard title="Largest Income Transactions" items = {largestIncomeTransactions} addPlus = "" />
                </div>

                {/* Income Graphs */}
                <div style={{ display: "flex", gap: "16px", flex: 1 }}>
                  <GraphCard 
                    title="Income Over Time" 
                    data = {periodIncome} 
                    lines = {[
                      { dataKey: "income", color: "#22c55e" }
                    ]}
                    dataKeyX = "period"
                  />
                  <PieGraphCard title="Income By Category" data = {categoryIncome} />
                </div>

                {/* Income Forecast */}
                <div>
                  <ForecastCard title="Next Period Forecast" />
                </div>

              </div>
            )}
            {currentView === "Expense" && (
              <div style={{ display: "flex", flexDirection: "column", gap: "16px", height: "100%" }}>

                {/* Major expense stats */}
                <div style={{ display: "flex", gap: "16px" }}>
                  <StatCard title="Total Expenses" value = {`$${totalExpenses.toLocaleString()}`} colour = "red"/>
                  <StatCard 
                    title="Change in Expenses" 
                    value = {`$${expenseChange.toLocaleString()} (${expenseChangePercent.toLocaleString()}%)`} 
                    colour= {expenseChange <= 0 ? "green" : "red"}
                  />
                  <TransactionRankCard title="Largest Expense Transactions" items = {largestExpenseTransactions} addPlus = "" />
                </div>

                {/* Expense graphs */}
                <div style={{ display: "flex", gap: "16px", flex: 1 }}>
                  <GraphCard 
                    title="Expenses Over Time" 
                    data = {periodExpenses} 
                    lines = {[
                      { dataKey: "expense", color: "#ef4444" }
                    ]}
                    dataKeyX = "period"
                  />
                  <PieGraphCard title="Expenses By Category" data = {categoryExpenses} />
                  <PieGraphCard title="Expenses By Type" data = {categorySpendingType} />
                </div>

                {/* Expense Forecast */}
                <div>
                  <ForecastCard title="Next Period Forecast" />
                </div>

              </div>
            )}
            {currentView === "Trends" && (
              <div style={{ display: "flex", flexDirection: "column", gap: "16px", height: "100%" }}>

                {/* Major trend stats */}
                <div style={{ display: "flex", gap: "16px" }}>
                  <TransactionRankCard title="Fastest Growing Expenses" items = {changingExpenses} addPlus = "+" />
                  <TransactionRankCard title="Fastest Growing Income" items = {changingIncome} addPlus = "+"  />
                </div>

                {/* Category Growth */}
                <div style={{ display: "flex", gap: "16px", flex: 1 }}>
                  <BarGraphCard 
                    title="Category Growth" 
                    data={categoryGrowth.map(item => ({ ...item, amount: Number(item.amount), colour: item.amount >= 0 ? "#22c55e" : "#ef4444" }))}
                    dataKeyX="category"
                    bars={[{ dataKey: "amount", color: "#3b82f6" }]}
                  />
                </div>

              </div>
            )}
          </div>
      </div>
    </div>
  );
}

// View Button Style
const viewButtonStyle = {
  padding: "12px 24px",
  backgroundColor: "#dbeeff",
  color: "black",
  borderRadius: "6px",
  fontSize: "16px",
  cursor: "pointer",
  width: "100%",
};

// Card for general stats
const StatCard = ({ title, value, colour }) => (
  <div style={{
    flex: 1,
    padding: "16px",
    backgroundColor: "#fff",
    borderRadius: "10px",
    boxShadow: "0 2px 4px rgba(0,0,0,0.08)",
    borderLeft: `6px solid ${colour}`
  }}>
    <h4>{title}</h4>
    <p style={{ fontSize: "24px", fontWeight: "bold", color: colour }}>
      {value}
    </p>
  </div>
);

// Card for ranking transactions
const TransactionRankCard = ({ title, items, addPlus }) => (
  <div style={{
    flex: 1,
    padding: "16px",
    backgroundColor: "#f9fafb",
    borderRadius: "10px",
    boxShadow: "0 2px 4px rgba(0,0,0,0.08)"
  }}>
    <h4>{title}</h4>

    {/* Header */}
    <div style={{
      display: "grid",
      gridTemplateColumns: "1fr 1fr 1fr",
      fontWeight: "600",
      fontSize: "12px",
      marginBottom: "6px"
    }}>
      <span>Category</span>
      <span>Amount</span>
      <span>Date</span>
    </div>

    {/* Rows */}
    {items.map((item, index) => (
      <div
        key={index}
        style={{
          display: "grid",
          gridTemplateColumns: "1fr 1fr 1fr",
          fontSize: "13px",
          padding: "4px 0",
          borderTop: "1px solid #e5e7eb"
        }}
      >
        <span>{item.category}</span>
        <span>{addPlus}${item.amount}</span>
        <span>{item.date}</span>
      </div>
    ))}
  </div>
);

const expenseData = [
  { month: "Jan", amount: 3200 },
  { month: "Feb", amount: 3100 },
  { month: "Mar", amount: 3500 }, 
  { month: "Apr", amount: 3900 },
];

// Pie charts
const PieGraphCard = ({ title, data }) => (
  <div style={{
    flex: 1,
    padding: "16px",
    backgroundColor: "#f9fafb",
    borderRadius: "10px",
    boxShadow: "0 2px 4px rgba(0,0,0,0.08)",
    display: "flex",
    flexDirection: "column"
  }}>
    <h4>{title}</h4>

    <div style={{ flex: 1 }}>
      <ResponsiveContainer width="100%" height="100%">
        <PieChart>
          <Pie
            data={data}
            dataKey="value"
            nameKey="name"
            outerRadius={90}
            label
          >
            {data.map((_, index) => (
              <Cell
                key={index}
                fill={["#3b82f6", "#22c55e", "#f59e0b", "#a855f7"][index % 4]}
              />
            ))}
          </Pie>
          <Legend />
        </PieChart>
      </ResponsiveContainer>
    </div>
  </div>
);

// Helper function to calculate regressionLine
const getRegression = (data, keyX, keyY) => {
  const n = data.length;
  if (n === 0) return [];

  // Converting X axis from dates to numbers
  const xVals = data.map((d, i) => i + 1); 
  const yVals = data.map(d => d[keyY] || 0);

  const sumX = xVals.reduce((a, b) => a + b, 0);
  const sumY = yVals.reduce((a, b) => a + b, 0);
  const sumXY = xVals.reduce((sum, x, i) => sum + x * yVals[i], 0);
  const sumX2 = xVals.reduce((sum, x) => sum + x * x, 0);

  const slope = (n * sumXY - sumX * sumY) / (n * sumX2 - sumX * sumX);
  const intercept = (sumY - slope * sumX) / n;

  return data.map((d, i) => ({
    ...d,
    [`${keyY}_regression`]: intercept + slope * (i + 1),
  }));
}

// Card for line graphs
const GraphCard = ({ title, data = [], lines = [], dataKeyX = "month" }) => {
  // Generate regression lines for all lines
  let regressionData = [...data];
  lines.forEach(line => {
    regressionData = getRegression(regressionData, dataKeyX, line.dataKey);
  });

  return (
    <div
      style={{
        flex: 1,
        padding: "16px",
        backgroundColor: "#f9fafb",
        borderRadius: "10px",
        boxShadow: "0 2px 4px rgba(0,0,0,0.08)",
        display: "flex",
        flexDirection: "column"
      }}
    >
      <h4>{title}</h4>
      <div style={{ flex: 1 }}>
        <ResponsiveContainer width="100%" height="100%">
          <LineChart data={regressionData}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey={dataKeyX} />
            <YAxis />
            <Tooltip 
               formatter={(value, name) => {
                if (name?.includes("_regression")) return null;
                return value;
              }}
            />
            <Legend />
            {lines.map((line, idx) => (
              <Line
                key={idx}
                type="monotone"
                dataKey={line.dataKey}
                stroke={line.color}
                strokeWidth={2}
              />
            ))}

            {/* Regression lines */}
            {lines.map((line, idx) => (
              <Line
                key={`regression-${idx}`}
                type="monotone"
                dataKey={`${line.dataKey}_regression`}
                stroke={line.color}
                strokeWidth={2}
                strokeDasharray="5 5"
                opacity={0.4}
                dot={false}
                legendType="none"
              />
            ))}
          </LineChart>
        </ResponsiveContainer>
      </div>
    </div>
  );
};

// Card for bar graphs
const BarGraphCard = ({ title, data = [], bars = [], dataKeyX = "month" }) => {
  return (
    <div
      style={{
        flex: 1,
        padding: "16px",
        backgroundColor: "#f9fafb",
        borderRadius: "10px",
        boxShadow: "0 2px 4px rgba(0,0,0,0.08)",
        display: "flex",
        flexDirection: "column"
      }}
    >
      <h4>{title}</h4>
      <div style={{ flex: 1 }}>
        <ResponsiveContainer width="100%" height="100%">
          <BarChart data={data}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey={dataKeyX} />
            <YAxis />
            <Tooltip />
            <Legend />
            
            {bars.map((bar, idx) => (
              <Bar
                key={idx}
                dataKey={bar.dataKey}
                fill={bar.color}
                minPointSize={2}       // ensures tiny negative values are visible
              />
            ))}
          </BarChart>
        </ResponsiveContainer>
      </div>
    </div>
  );
};

// Card for forecasts
const ForecastCard = ({ title }) => (
  <div style={{
    padding: "16px",
    backgroundColor: "#f9fafb",
    borderRadius: "10px",
    boxShadow: "0 2px 4px rgba(0,0,0,0.08)"
  }}>
    <h4>{title}</h4>
    <p>Forecast content here</p>
  </div>
);

export default Planner;
