import React, { useState, useEffect } from "react";
import { useNavigate, Link } from "react-router-dom";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
  PieChart,
  Pie,
  Cell,
  Legend
} from "recharts";

function Planner() {
  const navigate = useNavigate();

  // States
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [typeOption, setTypeOption] = useState("");
  const [categoryOption, setCategoryOption] = useState("");
  const [amountValue, setAmountValue] = useState("");
  const [dateValue, setDateValue] = useState("");
  const [descriptionValue, setDescriptionValue] = useState("");

  // View States
  const [currentView, setCurrentView] = useState("General");

  // General view states
  const [totalIncome, setTotalIncome] = useState(0);
  const [totalExpenses, setTotalExpenses] = useState(0);
  const [totalSavings, setTotalSavings] = useState(0);
  const [savingsRate, setSavingsRate] = useState(0);
  const [EIRatio, setEIRatio] = useState(0);
  const [emergencyFund, setEmergencyFund] = useState(0);
  const [runway, setRunway] = useState(0);

  // Income view states
  const [largestIncomeTransactions, setLargestIncomeTransactions] = useState([]);
  const [largestExpenseTransactions, setLargestExpenseTransactions] = useState([]);

  // Categories mapped by type
  const categories = {
    income: ["Salary", "Interest", "Investments", "Bonuses", "Other Income"],
    expense: [
      "Rent",
      "Utilities",
      "Insurance",
      "Health/Medical",
      "Education",
      "Travel",
      "Subscriptions",
      "Loan payment",
      "Leisure",
      "Clothes",
      "Food",
      "Other Expense"
    ],
    savings: ["Deposit", "Withdrawal"]
  };

  // Discretionary vs non discretionary mapping
  const spendingType = {
    discretionary: [
      "Travel",
      "Leisure",
      "Clothes",
      "Other Expense"
    ],
    nonDiscretionary: [
      "Rent",
      "Utilities",
      "Insurance",
      "Health/Medical",
      "Education",
      "Subscriptions",
      "Loan payment",
      "Savings",
      "Food"
    ]
  }

  const handleTransactionAdd = async () => {

    // Checking all fields are filled correctly

    // Transaction type is not blank
    if (!typeOption || !categoryOption || !amountValue || !dateValue) {
      setError("Required fields are empty");
      return;
    }

    // Amount is within bounds > 0 and < 1000000000 and if it has a $ or . it is removed and accounted for
    const amount = parseFloat(amountValue.replace("$", ""));
    if (isNaN(amount) || amount <= 0 || amount >= 1000000000) {
      setError("Invalid amount. Must be between $0 and $1 billion.");
      return;
    }

    // Date has correct format and is valid
    const dateRegex = /^(0?[1-9]|[12][0-9]|3[01])\/(0?[1-9]|1[0-2])\/\d{4}$/;
    if (!dateRegex.test(dateValue)) {
      setError("Invalid date.");
      return;
    }

    // Assigning spending type
    let spending = "NULL";
    if (spendingType.discretionary.includes(categoryOption)) {
      spending = "Discretionary";
    } else if (spendingType.nonDiscretionary.includes(categoryOption)) {
      spending = "Non-Discretionary";
    }

    // Storing the data
    try {
      const response = await fetch("http://localhost:5000/api/add", { 
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({ 
          type: typeOption, 
          category: categoryOption, 
          spendingType: spending,
          amount: amount, 
          date: dateValue, 
          description:descriptionValue 
        }),
      });

      const data = await response.json();

      if (!response.ok) {
        alert(data.message || "Adding transaction failed");
        return;
      }

      setSuccess("Added Transaction!");
      fetchData();

      // Resetting states
      setTypeOption("");
      setCategoryOption("");
      setAmountValue("");
      setDateValue("");
      setDescriptionValue("");
    } catch (error) {
      console.error("Error adding transaction:", error);
      setError("Network error.");
    }
  };

  // Going to the transactions page
  const handleTransactionEdit = async () => {
    navigate("/transactions")
  }

  // Changing stat views
  const handleViewChange = async (view) => {
    setCurrentView(view);
  }

  const fetchData = () => {
    fetchTotalIncome();
    fetchTotalExpenses();
    fetchTotalSavings();
    fetchLargestIncome();
    fetchLargestExpense();
  }

  // Fetch total income
  const fetchTotalIncome = () => {
    fetch(`http://localhost:5000/api/stats/total-income`, { credentials: "include" })
      .then(res => res.json())
      .then(data => setTotalIncome(data.total))
      .catch(err => console.error("Failed to fetch total income:", err));
  }

  // Fetch total expenses
  const fetchTotalExpenses = () => {
    fetch(`http://localhost:5000/api/stats/total-expenses`, { credentials: "include" })
      .then(res => res.json())
      .then(data => setTotalExpenses(data.total))
      .catch(err => console.error("Failed to fetch total expenses:", err));
  }

  // Fetch total savings
  const fetchTotalSavings = () => {
    fetch(`http://localhost:5000/api/stats/total-savings`, { credentials: "include" })
      .then(res => res.json())
      .then(data => setTotalSavings(data.total))
      .catch(err => console.error("Failed to fetch total savings:", err));
  }

  // Fetch largest income transactions
  const fetchLargestIncome = () => {
    fetch(`http://localhost:5000/api/stats/largest-income`, { credentials: "include" })
      .then(res => res.json())
      .then(data => {
        const formatted = data.topIncome.map(tx => ({
          ...tx,
          amount: Number(tx.amount).toFixed(2)
        }));
        setLargestIncomeTransactions(formatted)
      })
      .catch(err => console.error("Failed to fetch largest income transactions:", err));
  }

  // Fetch the largest expense transactions
  const fetchLargestExpense = () => {
    fetch(`http://localhost:5000/api/stats/largest-expense`, { credentials: "include" })
      .then(res => res.json())
      .then(data => {
        const formatted = data.topExpense.map(tx => ({
          ...tx,
          amount: Number(tx.amount).toFixed(2)
        }));
        setLargestExpenseTransactions(formatted)
      })
      .catch(err => console.error("Failed to fetch largest expense transactions:", err));
  }

  // Calculating other stats
  useEffect(() => {
    setEmergencyFund(((totalExpenses) * 6).toFixed(0));
    if (totalIncome > 0) {
      setSavingsRate((((totalIncome - totalExpenses) / totalIncome) * 100).toFixed(2));
      setEIRatio((totalExpenses / totalIncome).toFixed(2));
      setRunway((totalSavings / (totalExpenses)).toFixed(0))
    } else {
      setSavingsRate(0);
      setEIRatio(0);
      setRunway(0);
    }
  }, [totalIncome, totalExpenses]);

  // One-time fetch on component mount
  useEffect(() => {
    fetchData();
  }, []);

  return (
    <div>
      <style>
        {`
          body {
            margin: 0;
            padding: 0;
          }
        `}
      </style>

      {/* TOP TITLE RIBBON */}
      <div style={{
        position: "fixed",
        top: 0,
        left: 0,
        width: "100%",
        backgroundColor: "#bb4c0b",
        color: "white",
        padding: "18px",
        textAlign: "center",
        zIndex: 1000
      }}>
        <h1 style={{ margin: 0, letterSpacing: "2px" }}>
          FINANCIAL PLANNER
        </h1>
      </div>

      {/* Spacer so content is not hidden */}
      <div style={{ height: "60px" }} />

      <div style={{
        display: "flex",
        minHeight: "calc(100vh - 60px)",
        backgroundColor: "#dbeeff",
        fontFamily: "Arial, sans-serif"
      }}>

        {/* Left Ribbon */}
        <div style={{
          width: "300px",
          backgroundColor: "#003366",
          padding: "20px",
          color: "white",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
        }}>
          <h2>ENTER A TRANSACTION</h2>

          {/* Transaction Type Dropdown */}
          <select
            value={typeOption}
            onChange={(e) => {
              setTypeOption(e.target.value);
              setCategoryOption(""); // reset category when type changes
            }}
            onFocus={() => {
              setError("");
              setSuccess("");
            }}
            style={{
              padding: "10px",
              fontSize: "16px",
              borderRadius: "6px",
              border: "none",
              width: "100%",
              marginBottom: "20px"
            }}
          >
            <option value="">Transaction Type</option>
            <option value="income">Income</option>
            <option value="expense">Expense</option>
            <option value="savings">Savings</option>
          </select>

          {/* Category Dropdown */}
          <select
            value={categoryOption}
            onChange={(e) => setCategoryOption(e.target.value)}
            disabled={!typeOption} // disabled until type is selected
            onFocus={() => {
              setError("");
              setSuccess("");
            }}
            style={{
              padding: "10px",
              fontSize: "16px",
              borderRadius: "6px",
              border: "none",
              width: "100%",
              marginBottom: "20px"
            }}
          >
            <option value="">Category</option>
            {typeOption && categories[typeOption].map((cat, index) => (
              <option key={index} value={cat}>{cat}</option>
            ))}
          </select>

          {/* Amount */}
          <input
            type="text"
            placeholder="Amount"
            value={amountValue}
            onChange={(e) => setAmountValue(e.target.value)}
            onFocus={() => {
              setError("");
              setSuccess("");
            }}
            style={{
              padding: "10px",
              fontSize: "16px",
              borderRadius: "6px",
              border: "none",
              width: "94%",
              marginBottom: "20px"
            }}
          />

          {/* Date */}
          <input
            type="text"
            placeholder="Date (DD/MM/YYYY)"
            value={dateValue}
            onChange={(e) => setDateValue(e.target.value)}
            onFocus={() => {
              setError("");
              setSuccess("");
            }}
            style={{
              padding: "10px",
              fontSize: "16px",
              borderRadius: "6px",
              border: "none",
              width: "94%",
              marginBottom: "20px"
            }}
          />

          {/* Brief Description */}
          <textarea
            placeholder="Description (optional)"
            value={descriptionValue}
            onChange={(e) => setDescriptionValue(e.target.value)}
            onFocus={() => {
              setError("");
              setSuccess("");
            }}
            rows={5} // show 5 lines by default
            style={{
              padding: "10px",
              fontSize: "16px",
              borderRadius: "6px",
              border: "none",
              width: "94%",
              marginBottom: "20px",
              resize: "vertical",
              flex: 1
            }}
          />

          {/* Button Container */}
          <div style={{ display: "flex", gap: "10px", marginTop: "0px" }}>
            {/* ADD Button */}
            <button
              onFocus={() => {
                setError("");
                setSuccess("");
              }}
              onClick={handleTransactionAdd}
              style={{
                padding: "12px 24px",
                backgroundColor: "#dbeeff",
                color: "black",
                borderRadius: "6px",
                fontSize: "16px",
                cursor: "pointer",
                flex: 1 // optional, makes both buttons same width
              }}
            >
              Add
            </button>

            {/* EDIT Button */}
            <button
              onFocus={() => {
                setError("");
                setSuccess("");
              }}
              onClick={handleTransactionEdit}
              style={{
                padding: "12px 24px",
                backgroundColor: "#dbeeff",
                color: "black",
                borderRadius: "6px",
                fontSize: "16px",
                cursor: "pointer",
                flex: 1 // optional, makes both buttons same width
              }}
            >
              Edit
            </button>
          </div>


          {/* Error Message */}
          {error && (
            <p style={{ color: "red", marginTop: "10px", fontWeight: "bold" }}>
              {error}
            </p>
          )}

          {/* Success Message */}
          {success && (
            <p style={{ color: "green", marginTop: "10px", fontWeight: "bold" }}>
              {success}
            </p>
          )}

          {/* View Buttons*/}
          <div style={{ display: "flex", flexDirection: "column", width: "100%", gap: "12px", marginTop: "150px", marginBottom: "20px"  }}>
            {["General","Income","Expense","Trends"].map((label) => (
              <button
                key={label}
                onClick={() => handleViewChange(label)}
                style={{
                  ...viewButtonStyle,
                  backgroundColor: currentView === label ? "#5a8fcf" : "#dbeeff"
                }}
              >
                {label}
              </button>
            ))}
          </div>
          </div>

          {/* Main Stats View */}
          <div style={{
            flex: 1,
            padding: "20px",
            backgroundColor: "#ffffff",
            borderRadius: "12px",
            boxShadow: "0 4px 8px rgba(0,0,0,0.1)",
            display: "flex",
            flexDirection: "column"
          }}>
            {currentView === "General" && (
              <div style={{ display: "flex", flexDirection: "column", gap: "16px", height: "100%" }}>

                {/* Main General Stats */}
                <div style={{ display: "flex", gap: "16px" }}>
                  <StatCard 
                    title="Total Savings" 
                    value = {`$${totalSavings.toLocaleString()}`} 
                    colour= { totalSavings>= 0 ? "green" : "red" } />
                  <StatCard title="Total Income" value = {`$${totalIncome.toLocaleString()}`} colour = "green" />
                  <StatCard title="Total Expenses" value = {`-$${totalExpenses.toLocaleString()}`} colour = "red" />
                  <StatCard 
                    title="Net Cashflow" 
                    value = {`$${(totalIncome - totalExpenses).toLocaleString()}`} 
                    colour= {totalIncome - totalExpenses >= 0 ? "green" : "red"}
                  />
                </div>

                {/* Graphs */}
                <div style={{ display: "flex", gap: "16px", flex: 1 }}>
                  <GraphCard title="Income vs Expenses" data = {incomeData} />
                  <GraphCard title="Net Cashflow" data = {expenseData} />
                </div>

                {/* Other General Stats */}
                <div style={{ display: "flex", gap: "16px" }}>
                  <StatCard 
                    title="Savings Rate" 
                    value = {`${savingsRate.toLocaleString()}%`} 
                    colour= {savingsRate >= 0 ? "green" : "red"}
                  />
                  <StatCard 
                    title="Expense / Income Ratio" 
                    value = {`${EIRatio.toLocaleString()} : 1`} 
                    colour = {EIRatio <= 0.8 ? "green" : "red"}
                  />
                  <StatCard 
                    title="Emergency Fund Estimate" 
                    value = {`$${emergencyFund.toLocaleString()}`}
                    colour = {emergencyFund <= totalSavings ? "green" : "red"}
                  />
                  <StatCard 
                    title="Months of Runway" 
                    value = {`${runway.toLocaleString()} months`}
                    colour = {runway >= 6 ? "green" : "red"}
                  />
                </div>

                {/* Forecast */}
                <div>
                  <ForecastCard title="Next Month Forecast" />
                </div>

              </div>
            )}
            {currentView === "Income" && (
              <div style={{ display: "flex", flexDirection: "column", gap: "16px", height: "100%" }}>

                {/* Major Income stats */}
                <div style={{ display: "flex", gap: "16px" }}>
                  <StatCard title="Total Income" value = {`$${totalIncome.toLocaleString()}`} colour = "green"/>
                  <StatCard title="Change in Income" value = "$0000" colour = "black"/>
                  <TransactionRankCard title="Largest Income Transactions" items = {largestIncomeTransactions} />
                </div>

                {/* Income Graphs */}
                <div style={{ display: "flex", gap: "16px", flex: 1 }}>
                  <GraphCard title="Income Over Time" data = {incomeData} />
                  <PieGraphCard title="Income By Category" data = {incomeByCategoryData} />
                </div>

                {/* Income Forecast */}
                <div>
                  <ForecastCard title="Next Month Forecast" />
                </div>

              </div>
            )}
            {currentView === "Expense" && (
              <div style={{ display: "flex", flexDirection: "column", gap: "16px", height: "100%" }}>

                {/* Major expense stats */}
                <div style={{ display: "flex", gap: "16px" }}>
                  <StatCard title="Total Expenses" value = {`$${totalExpenses.toLocaleString()}`} colour = "red"/>
                  <StatCard title="Change in Expenses" value = "$0000"colour = "black" />
                  <TransactionRankCard title="Largest Expense Transactions" items = {largestExpenseTransactions} />
                </div>

                {/* Expense graphs */}
                <div style={{ display: "flex", gap: "16px", flex: 1 }}>
                  <GraphCard title="Expenses Over Time" data = {expenseData} />
                  <PieGraphCard title="Expenses By Category" data = {expensesByCategoryData} />
                  <PieGraphCard title="Expenses By Type" data = {expensesByTypeData} />
                </div>

                {/* Expense Forecast */}
                <div>
                  <ForecastCard title="Next Month Forecast" />
                </div>

              </div>
            )}
            {currentView === "Trends" && (
              <div style={{ display: "flex", flexDirection: "column", gap: "16px", height: "100%" }}>

                {/* Major trend stats */}
                <div style={{ display: "flex", gap: "16px" }}>
                  <TransactionRankCard title="Month-to-Month Change" items = {largestIncomeTransactions} />
                  <TransactionRankCard title="Fastest Growing Expenses" items = {largestIncomeTransactions} />
                  <TransactionRankCard title="Fastest Growing Income" items = {largestIncomeTransactions} />
                </div>

                {/* Trend Graphs */}
                <div style={{ display: "flex", gap: "16px", flex: 1 }}>
                  <GraphCard title="Rolling Average (6 month)" data = {expenseData} />
                  <GraphCard title="Category Growth" data = {expenseData} />
                </div>

                {/* Regression */}
                <div style={{ display: "flex", gap: "16px", flex: 1 }}>
                  <GraphCard title="Regression" data={expenseData} />
                </div>

              </div>
            )}
          </div>
      </div>
    </div>
  );
}

// View Button Style
const viewButtonStyle = {
  padding: "12px 24px",
  backgroundColor: "#dbeeff",
  color: "black",
  borderRadius: "6px",
  fontSize: "16px",
  cursor: "pointer",
  width: "100%",
};

// Card for general stats
const StatCard = ({ title, value, colour }) => (
  <div style={{
    flex: 1,
    padding: "16px",
    backgroundColor: "#fff",
    borderRadius: "10px",
    boxShadow: "0 2px 4px rgba(0,0,0,0.08)",
    borderLeft: `6px solid ${colour}`
  }}>
    <h4>{title}</h4>
    <p style={{ fontSize: "24px", fontWeight: "bold", color: colour }}>
      {value}
    </p>
  </div>
);

// Card for ranking transactions
const TransactionRankCard = ({ title, items }) => (
  <div style={{
    flex: 1,
    padding: "16px",
    backgroundColor: "#f9fafb",
    borderRadius: "10px",
    boxShadow: "0 2px 4px rgba(0,0,0,0.08)"
  }}>
    <h4>{title}</h4>

    {/* Header */}
    <div style={{
      display: "grid",
      gridTemplateColumns: "1fr 1fr 1fr",
      fontWeight: "600",
      fontSize: "12px",
      marginBottom: "6px"
    }}>
      <span>Category</span>
      <span>Amount</span>
      <span>Date</span>
    </div>

    {/* Rows */}
    {items.map((item, index) => (
      <div
        key={index}
        style={{
          display: "grid",
          gridTemplateColumns: "1fr 1fr 1fr",
          fontSize: "13px",
          padding: "4px 0",
          borderTop: "1px solid #e5e7eb"
        }}
      >
        <span>{item.category}</span>
        <span>${item.amount}</span>
        <span>{item.date}</span>
      </div>
    ))}
  </div>
);

const expensesByCategoryData = [
  { name: "Rent", value: 1800 },
  { name: "Groceries", value: 520 },
  { name: "Transport", value: 260 },
  { name: "Utilities", value: 210 },
  { name: "Dining Out", value: 190 },
  { name: "Entertainment", value: 150 },
  { name: "Subscriptions", value: 80 },
  { name: "Other", value: 120 }
];

const expensesByTypeData = [
  { name: "Non-Discretionary", value: 2600 },  // rent, utilities, groceries, transport
  { name: "Discretionary", value: 900 }       // dining, entertainment, shopping, etc
];

const incomeByCategoryData = [
  { name: "Salary", value: 5000 },
  { name: "Freelance", value: 1200 },
  { name: "Investments", value: 800 },
  { name: "Other", value: 400 }
];

const incomeData = [
  { month: "Jan", amount: 4000 },
  { month: "Feb", amount: 4200 },
  { month: "Mar", amount: 4800 },
  { month: "Apr", amount: 5200 },
];

const expenseData = [
  { month: "Jan", amount: 3200 },
  { month: "Feb", amount: 3100 },
  { month: "Mar", amount: 3500 }, 
  { month: "Apr", amount: 3900 },
];

// Pie charts
const PieGraphCard = ({ title, data }) => (
  <div style={{
    flex: 1,
    padding: "16px",
    backgroundColor: "#f9fafb",
    borderRadius: "10px",
    boxShadow: "0 2px 4px rgba(0,0,0,0.08)",
    display: "flex",
    flexDirection: "column"
  }}>
    <h4>{title}</h4>

    <div style={{ flex: 1 }}>
      <ResponsiveContainer width="100%" height="100%">
        <PieChart>
          <Pie
            data={data}
            dataKey="value"
            nameKey="name"
            outerRadius={90}
            label
          >
            {data.map((_, index) => (
              <Cell
                key={index}
                fill={["#3b82f6", "#22c55e", "#f59e0b", "#a855f7"][index % 4]}
              />
            ))}
          </Pie>
          <Legend />
        </PieChart>
      </ResponsiveContainer>
    </div>
  </div>
);

// Card for line graphs
const GraphCard = ({ title, data }) => (
  <div style={{
    flex: 1,
    padding: "16px",
    backgroundColor: "#f9fafb",
    borderRadius: "10px",
    boxShadow: "0 2px 4px rgba(0,0,0,0.08)",
    display: "flex",
    flexDirection: "column"
  }}>
    <h4>{title}</h4>
    <div style={{ flex: 1 }}>
      <ResponsiveContainer width="100%" height="100%">
        <LineChart data={data}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="month" />
          <YAxis />
          <Tooltip />
          <Line
            type="monotone"
            dataKey="amount"
            stroke="#3b82f6"
            strokeWidth={2}
          />
        </LineChart>
      </ResponsiveContainer>
    </div>
  </div>
);

// Card for forecasts
const ForecastCard = ({ title }) => (
  <div style={{
    padding: "16px",
    backgroundColor: "#f9fafb",
    borderRadius: "10px",
    boxShadow: "0 2px 4px rgba(0,0,0,0.08)"
  }}>
    <h4>{title}</h4>
    <p>Forecast content here</p>
  </div>
);

export default Planner;
