import React, { useState, useEffect } from "react";
import usePlannerData from "../hooks/usePlannerData";
import { useNavigate, Link } from "react-router-dom";

// Helper Functions
import { formatCurrentDate, formatPreviousDate } from "../utils/dateHelpers";
import { validateTransaction, getSpendingType } from "../utils/plannerHelpers";

// Constants
import { categories, spendingTypeMap, monthNames } from "../constants/PlannerConstants.js";

// Views
import GeneralView from "../components/planner/GeneralView";
import IncomeView from "../components/planner/IncomeView";
import ExpenseView from "../components/planner//ExpenseView";
import TrendsView from "../components/planner/TrendsView";

// Shared components
import ViewSelector from "../components/planner/ViewSelector";

function Planner() {
  const navigate = useNavigate();

  // States
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [typeOption, setTypeOption] = useState("");
  const [categoryOption, setCategoryOption] = useState("");
  const [amountValue, setAmountValue] = useState("");
  const [dateValue, setDateValue] = useState("");
  const [descriptionValue, setDescriptionValue] = useState("");

  // View Date Range
  const [yearValue, setYearValue] = useState("2026");
  const [prevYearValue, setPrevYearValue] = useState("2026");
  const [monthValue, setMonthValue] = useState("01");
  const [dateRangeOption, setDateRangeOption] = useState("monthly");

  // Dates
  const [formattedDate, setFormattedDate] = useState("");
  const [oldDate, setOldDate] = useState("");

  // View States
  const [currentView, setCurrentView] = useState("General");

  // Formatting the date
  const updateDateView = () => {
    setFormattedDate(formatCurrentDate(yearValue, monthValue, dateRangeOption));
    setOldDate(formatPreviousDate(yearValue, monthValue, dateRangeOption));
  };

  // One time fetch
  useEffect(() => {
    updateDateView();
  }, [yearValue, monthValue, dateRangeOption])

  // Defining variables for use
  const {
    totalIncome,
    totalExpenses,
    prevIncome,
    prevExpenses,
    totalSavings,
    categoryIncome,
    categoryExpenses,
    spendingType: categorySpendingType,
    transactions: periodTransactions,
    largestIncome,
    largestExpense,
    fastestGrowingIncome: changingIncome,
    fastestGrowingExpenses: changingExpenses,
    categoryGrowth,
    loading,
    error: fetchError,
    refetch
  } = usePlannerData(formattedDate, oldDate);

  console.log(totalIncome);

  // -- CALCULATING STATS--
  const emergencyFund = totalExpenses * 6;

  const incomeChange = totalIncome - prevIncome;
  const incomeChangePercent =
    prevIncome !== 0 ? ((incomeChange / prevIncome) * 100).toFixed(0) : 0;

  const expenseChange = totalExpenses - prevExpenses;
  const expenseChangePercent =
    prevExpenses !== 0 ? ((expenseChange / prevExpenses) * 100).toFixed(0) : 0;

  const periodCashflow = periodTransactions.map(item => ({
    period: item.period,
    net: item.income - item.expense
  }));

  const periodIncome = periodTransactions.map(item => ({
    period: item.period,
    income: item.income
  }));

  const periodExpenses = periodTransactions.map(item => ({
    period: item.period,
    expense: item.expense
  }));

  const savingsRate = totalIncome > 0 ? (((totalIncome - totalExpenses) / totalIncome) * 100).toFixed(2) : 0;
  const EIRatio = totalIncome > 0 ? (totalExpenses / totalIncome).toFixed(2) : 0;
  const runway = totalExpenses > 0 ? Math.floor(totalSavings / totalExpenses) : 0;

  // Categories mapped by type
  const categories = {
    income: ["Salary", "Interest", "Investments", "Bonuses", "Other Income"],
    expense: ["Rent", "Utilities", "Insurance", "Health/Medical", "Education", "Travel", "Subscriptions", "Loan payment", "Leisure", "Clothes", "Food", "Other Expense"],
    savings: ["Deposit", "Withdrawal"]
  };

  // Discretionary vs non discretionary mapping
  const spendingType = {
    discretionary: ["Travel", "Leisure", "Clothes", "Other Expense"],
    nonDiscretionary: ["Rent", "Utilities", "Insurance", "Health/Medical", "Education", "Subscriptions", "Loan payment", "Savings", "Food"]
  }

  // Updating the view 
  const handleViewChange = (view) => setCurrentView(view);

  const handleTransactionAdd = async () => {
    setError("");
    setSuccess("");

    // Validate inputs
    const errorMsg = validateTransaction({
      type: typeOption,
      category: categoryOption,
      amount: amountValue,
      date: dateValue
    });

    if (errorMsg) {
      setError(errorMsg);
      return;
    }

    const amount = parseFloat(amountValue.replace(/[$,]/g, ""));
    const spending = getSpendingType(categoryOption);

    try {
      const response = await fetch("http://localhost:5000/api/add", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({
          type: typeOption,
          category: categoryOption,
          spendingType: spending,
          amount,
          date: dateValue,
          description: descriptionValue,
        }),
      });

      const data = await response.json();

      if (!response.ok) {
        alert(data.message || "Adding transaction failed");
        return;
      }

      setSuccess("Added Transaction!");

      // Reset input states
      setTypeOption("");
      setCategoryOption("");
      setAmountValue("");
      setDateValue("");
      setDescriptionValue("");

      updateDateView(); 
    } catch (err) {
      console.error("Error adding transaction:", err);
      setError("Network error.");
    }
  };

  // Going to the transactions page
  const handleTransactionEdit = async () => {
    navigate("/transactions")
  }

  // --- Render Selected View ---
  const renderView = () => {
    switch (currentView) {
      case "Income":
        return <IncomeView
          totalIncome={totalIncome}
          incomeChange={incomeChange}
          incomeChangePercent={incomeChangePercent}
          largestIncome={largestIncome}
          periodIncome={periodIncome}
          categoryIncome={categoryIncome}
        />;
      case "Expense":
        return <ExpenseView
          totalExpenses={totalExpenses}
          expenseChange={expenseChange}
          expenseChangePercent={expenseChangePercent}
          largestExpense={largestExpense}
          periodExpenses={periodExpenses}
          categoryExpenses={categoryExpenses}
          categorySpendingType={categorySpendingType}
        />;
      case "Trends":
        return <TrendsView
          changingIncome={changingIncome}
          changingExpenses={changingExpenses}
          categoryGrowth={categoryGrowth}
        />;
      default: // General
        return <GeneralView
          totalIncome={totalIncome}
          totalExpenses={totalExpenses}
          totalSavings={totalSavings}
          periodTransactions={periodTransactions}
          savingsRate={savingsRate}
          EIRatio={EIRatio}
          emergencyFund={emergencyFund}
          runway={runway}
        />;
    }
  };

  return (
    <div>
      <style>{`body { margin: 0; padding: 0; }`}</style>

      {/* Top Ribbon */}
      <div
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100%",
          backgroundColor: "#bb4c0b",
          color: "white",
          padding: "18px",
          textAlign: "center",
          zIndex: 1000,
        }}
      >
        <h1 style={{ margin: 0, letterSpacing: "2px" }}>FINANCIAL PLANNER</h1>
      </div>

      {/* Spacer */}
      <div style={{ height: "60px" }} />

      <div
        style={{
          display: "flex",
          minHeight: "calc(100vh - 60px)",
          backgroundColor: "#dbeeff",
          fontFamily: "Arial, sans-serif",
        }}
      >
        {/* Left Panel */}
        <div
          style={{
            width: "300px",
            backgroundColor: "#003366",
            padding: "20px",
            color: "white",
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
          }}
        >
          <h2>ENTER A TRANSACTION</h2>

          {/* Transaction Type */}
          <select
            value={typeOption}
            onChange={(e) => {
              setTypeOption(e.target.value);
              setCategoryOption("");
            }}
            onFocus={() => {
              setError("");
              setSuccess("");
            }}
            style={{
              padding: "10px",
              fontSize: "16px",
              borderRadius: "6px",
              border: "none",
              width: "100%",
              marginBottom: "20px",
            }}
          >
            <option value="">Transaction Type</option>
            <option value="income">Income</option>
            <option value="expense">Expense</option>
            <option value="savings">Savings</option>
          </select>

          {/* Category */}
          <select
            value={categoryOption}
            onChange={(e) => setCategoryOption(e.target.value)}
            disabled={!typeOption}
            onFocus={() => {
              setError("");
              setSuccess("");
            }}
            style={{
              padding: "10px",
              fontSize: "16px",
              borderRadius: "6px",
              border: "none",
              width: "100%",
              marginBottom: "20px",
            }}
          >
            <option value="">Category</option>
            {typeOption &&
              categories[typeOption].map((cat, index) => (
                <option key={index} value={cat}>
                  {cat}
                </option>
              ))}
          </select>

          {/* Amount */}
          <input
            type="text"
            placeholder="Amount"
            value={amountValue}
            onChange={(e) => setAmountValue(e.target.value)}
            onFocus={() => {
              setError("");
              setSuccess("");
            }}
            style={{
              padding: "10px",
              fontSize: "16px",
              borderRadius: "6px",
              border: "none",
              width: "94%",
              marginBottom: "20px",
            }}
          />

          {/* Date */}
          <input
            type="text"
            placeholder="Date (DD/MM/YYYY)"
            value={dateValue}
            onChange={(e) => setDateValue(e.target.value)}
            onFocus={() => {
              setError("");
              setSuccess("");
            }}
            style={{
              padding: "10px",
              fontSize: "16px",
              borderRadius: "6px",
              border: "none",
              width: "94%",
              marginBottom: "20px",
            }}
          />

          {/* Description */}
          <textarea
            placeholder="Description (optional)"
            value={descriptionValue}
            onChange={(e) => setDescriptionValue(e.target.value)}
            onFocus={() => {
              setError("");
              setSuccess("");
            }}
            rows={5}
            style={{
              padding: "10px",
              fontSize: "16px",
              borderRadius: "6px",
              border: "none",
              width: "94%",
              marginBottom: "20px",
              resize: "vertical",
              flex: 1,
            }}
          />

          {/* Buttons */}
          <div style={{ display: "flex", gap: "10px", marginTop: "0px" }}>
            <button
              onClick={handleTransactionAdd}
              style={{
                padding: "12px 24px",
                backgroundColor: "#dbeeff",
                color: "black",
                borderRadius: "6px",
                fontSize: "16px",
                cursor: "pointer",
                flex: 1,
              }}
            >
              Add
            </button>

            <button
              onClick={handleTransactionEdit}
              style={{
                padding: "12px 24px",
                backgroundColor: "#dbeeff",
                color: "black",
                borderRadius: "6px",
                fontSize: "16px",
                cursor: "pointer",
                flex: 1,
              }}
            >
              Edit
            </button>
          </div>

          {/* Error/Success */}
          {error && (
            <p style={{ color: "red", marginTop: "10px", fontWeight: "bold" }}>
              {error}
            </p>
          )}
          {success && (
            <p style={{ color: "green", marginTop: "10px", fontWeight: "bold" }}>
              {success}
            </p>
          )}

          {/* View Range */}
          <div style={{ display: "flex", marginTop: "40px", marginBottom: "10px" }}>
            {["monthly", "yearly", ""].map((range) => (
              <button
                key={range}
                onClick={() => setDateRangeOption(range)}
                style={{
                  padding: "12px 24px",
                  backgroundColor: dateRangeOption === range ? "#5a8fcf" : "#dbeeff",
                  color: "black",
                  fontSize: "16px",
                  cursor: "pointer",
                  flex: 1,
                }}
              >
                {range === "monthly" ? "Monthly" : range === "yearly" ? "Yearly" : "Overall"}
              </button>
            ))}
          </div>

          {/* Month/Year Select */}
          <div style={{ display: "flex", gap: "12px", width: "240px" }}>
            <select
              value={monthValue}
              onChange={(e) => setMonthValue(e.target.value)}
              disabled={dateRangeOption !== "monthly"}
              style={{
                padding: "10px",
                fontSize: "16px",
                borderRadius: "6px",
                border: "none",
                width: "54%",
              }}
            >
              {Array.from({ length: 12 }, (_, i) => {
                const val = (i + 1).toString().padStart(2, "0");
                return (
                  <option key={val} value={val}>
                    {new Date(0, i).toLocaleString("default", { month: "long" })}
                  </option>
                );
              })}
            </select>

            <input
              type="number"
              value={yearValue}
              min={1976}
              max={2076}
              onChange={(e) => setYearValue(e.target.value)}
              disabled={dateRangeOption === ""} 
              onFocus={() => {
                setError("");
                setSuccess("");
              }}
              onBlur={() => {
                const numericValue = Number(yearValue);
                if (numericValue < 1976 || numericValue > 2076 || isNaN(numericValue)) {
                  setYearValue(prevYearValue);
                } else {
                  setPrevYearValue(numericValue);
                }
                updateDateView();
              }}
              style={{
                padding: "10px",
                fontSize: "16px",
                borderRadius: "6px",
                border: "none",
                width: "46%",
              }}
            />
          </div>

          {/* View Selector */}
          <ViewSelector currentView={currentView} handleViewChange={handleViewChange} />
        </div>

        {/* Main Content */}
        <div style={{ flex: 1, padding: "20px", backgroundColor: "#ffffff" }}>
          {renderView()}
        </div>
      </div>
    </div>
  );
}

export default Planner;
