import React from "react";

const TransactionForm = ({
  typeOption, setTypeOption,
  categoryOption, setCategoryOption,
  amountValue, setAmountValue,
  dateValue, setDateValue,
  descriptionValue, setDescriptionValue,
  handleTransactionAdd,
  handleTransactionEdit,
  error, setError,
  success, setSuccess,
  categories,
  dateRangeOption, setDateRangeOption,
  monthValue, setMonthValue,
  yearValue, setYearValue,
  prevYearValue, setPrevYearValue,
  updateDateView
}) => {
  return (
    <div style={{
      width: "300px",
      backgroundColor: "#003366",
      padding: "20px",
      color: "white",
      display: "flex",
      flexDirection: "column",
      alignItems: "center",
    }}>
      <h2>ENTER A TRANSACTION</h2>

      {/* Transaction Type */}
      <select
        value={typeOption}
        onChange={(e) => {
          setTypeOption(e.target.value);
          setCategoryOption("");
        }}
        onFocus={() => { setError(""); setSuccess(""); }}
        style={styles.inputFull}
      >
        <option value="">Transaction Type</option>
        <option value="income">Income</option>
        <option value="expense">Expense</option>
        <option value="savings">Savings</option>
      </select>

      {/* Category */}
      <select
        value={categoryOption}
        onChange={(e) => setCategoryOption(e.target.value)}
        disabled={!typeOption}
        onFocus={() => { setError(""); setSuccess(""); }}
        style={styles.inputFull}
      >
        <option value="">Category</option>
        {typeOption &&
          categories[typeOption].map((cat, index) => (
            <option key={index} value={cat}>{cat}</option>
          ))}
      </select>

      {/* Amount */}
      <input
        type="text"
        placeholder="Amount"
        value={amountValue}
        onChange={(e) => setAmountValue(e.target.value)}
        onFocus={() => { setError(""); setSuccess(""); }}
        style={styles.inputInset}
      />

      {/* Date */}
      <input
        type="text"
        placeholder="Date (DD/MM/YYYY)"
        value={dateValue}
        onChange={(e) => setDateValue(e.target.value)}
        onFocus={() => { setError(""); setSuccess(""); }}
        style={styles.inputInset}
      />

      {/* Description */}
      <textarea
        placeholder="Description (optional)"
        value={descriptionValue}
        onChange={(e) => setDescriptionValue(e.target.value)}
        onFocus={() => { setError(""); setSuccess(""); }}
        rows={5}
        style={styles.textarea}
      />

      {/* Action Buttons */}
      <div style={{ display: "flex", gap: "10px", width: "100%" }}>
        <button onClick={handleTransactionAdd} style={styles.actionButton}>Add</button>
        <button onClick={handleTransactionEdit} style={styles.actionButton}>Edit</button>
      </div>

      {/* Feedback Messages */}
      {error && <p style={styles.errorText}>{error}</p>}
      {success && <p style={styles.successText}>{success}</p>}

      {/* View Range Logic */}
      <div style={{ display: "flex", marginTop: "40px", marginBottom: "10px", width: "100%" }}>
        {["monthly", "yearly", ""].map((range) => (
          <button
            key={range}
            onClick={() => setDateRangeOption(range)}
            style={{
              ...styles.rangeButton,
              backgroundColor: dateRangeOption === range ? "#5a8fcf" : "#dbeeff",
            }}
          >
            {range === "monthly" ? "Monthly" : range === "yearly" ? "Yearly" : "Overall"}
          </button>
        ))}
      </div>

      {/* Date Selectors */}
      <div style={{ display: "flex", gap: "12px", width: "100%" }}>
        <select
          value={monthValue}
          onChange={(e) => setMonthValue(e.target.value)}
          disabled={dateRangeOption !== "monthly"}
          style={{ ...styles.inputFull, width: "54%", marginBottom: 0 }}
        >
          {Array.from({ length: 12 }, (_, i) => {
            const val = (i + 1).toString().padStart(2, "0");
            return (
              <option key={val} value={val}>
                {new Date(0, i).toLocaleString("default", { month: "long" })}
              </option>
            );
          })}
        </select>

        <input
          type="number"
          value={yearValue}
          min={1976}
          max={2076}
          onChange={(e) => setYearValue(e.target.value)}
          disabled={dateRangeOption === ""}
          onBlur={() => {
            const numericValue = Number(yearValue);
            if (numericValue < 1976 || numericValue > 2076 || isNaN(numericValue)) {
              setYearValue(prevYearValue);
            } else {
              setPrevYearValue(numericValue);
            }
            updateDateView();
          }}
          style={{ ...styles.inputFull, width: "46%", marginBottom: 0 }}
        />
      </div>
    </div>
  );
};

const styles = {
  inputFull: { padding: "10px", fontSize: "16px", borderRadius: "6px", border: "none", width: "100%", marginBottom: "20px" },
  inputInset: { padding: "10px", fontSize: "16px", borderRadius: "6px", border: "none", width: "94%", marginBottom: "20px" },
  textarea: { padding: "10px", fontSize: "16px", borderRadius: "6px", border: "none", width: "94%", marginBottom: "20px", resize: "vertical" },
  actionButton: { padding: "12px 24px", backgroundColor: "#dbeeff", color: "black", borderRadius: "6px", fontSize: "16px", cursor: "pointer", flex: 1 },
  rangeButton: { padding: "12px 10px", color: "black", fontSize: "14px", cursor: "pointer", flex: 1, border: "1px solid #ccc" },
  errorText: { color: "red", marginTop: "10px", fontWeight: "bold" },
  successText: { color: "green", marginTop: "10px", fontWeight: "bold" }
};

export default TransactionForm;
