// Formatting the current date
export const formatCurrentDate = (year, month, rangeOption) => {
  if (rangeOption === "monthly") return `${year}-${month}`;
  if (rangeOption === "yearly") return `${year}`;
  return "overall";
};

// Formatting the previous date
export const formatPreviousDate = (year, month, rangeOption) => {
  if (rangeOption === "monthly") {
    let y = parseInt(year, 10);
    let m = parseInt(month, 10) - 1;
    if (m === 0) {
      m = 12;
      y -= 1;
    }
    return `${y}-${String(m).padStart(2, "0")}`;
  }
  if (rangeOption === "yearly") return String(year - 1);
  return "overall";
};
