// Validating the new transaction to ensure all inputs are correct
export const validateTransaction = ({ type, category, amount, date }) => {
  if (!type || !category || !amount || !date) return "Required fields are empty";

  const numericAmount = parseFloat(amount.replace(/[$,]/g, ""));
  if (isNaN(numericAmount) || numericAmount <= 0 || numericAmount >= 1_000_000_000) {
    return "Invalid amount. Must be between $0 and $1 billion.";
  }

  const dateRegex = /^(0?[1-9]|[12][0-9]|3[01])\/(0?[1-9]|1[0-2])\/\d{4}$/;
  if (!dateRegex.test(date)) return "Invalid date.";

  const year = parseInt(date.split("/")[2], 10);
  if (year < 1976 || year > 2076) return "Year must be between 1976 and 2076.";

  return null;
};
