import React, { useEffect, useState } from "react";
import { useNavigate, Link } from "react-router-dom";



function Transactions() {
    const navigate = useNavigate();

    const [transactions, setTransactions] = useState([]);
    const [typeSortDir, setTypeSortDir] = useState("asc");
    const [categorySortDir, setCategorySortDir] = useState("asc");
    const [spendingTypeSortDir, setSpendingTypeSortDir] = useState("asc");
    const [amountSortDir, setAmountSortDir] = useState("asc");
    const [dateSortDir, setDateSortDir] = useState("asc");
    const [error, setError] = useState("");

    // Categories mapped by type
    const categories = {
        income: ["Salary", "Interest", "Investments", "Bonuses", "Other Income"],
        expense: [
        "Rent",
        "Utilities",
        "Insurance",
        "Health/Medical",
        "Education",
        "Travel",
        "Subscriptions",
        "Loan payment",
        "Savings",
        "Leisure",
        "Clothes",
        "Food",
        "Other Expense"
        ]
    };

    // Discretionary vs non discretionary mapping
    const spendingType = {
        discretionary: [
        "Travel",
        "Leisure",
        "Clothes",
        "Other Expense"
        ],
        nonDiscretionary: [
        "Rent",
        "Utilities",
        "Insurance",
        "Health/Medical",
        "Education",
        "Subscriptions",
        "Loan payment",
        "Savings",
        "Food"
        ]
    }

    // Editing states
    const [editingTransaction, setEditingTransaction] = useState(null);
    const [editForm, setEditForm] = useState({
        type: "",
        category: "",
        spendingType: "",
        amount: "",
        date: "",
        description: ""
    });
    const [editError, setEditError] = useState("");

    // Loads transactions when the page opens
    useEffect(() => {
        fetchTransactions();
    }, []);

    // Fetching transactions
    const fetchTransactions = async () => {
        try {
        const response = await fetch("http://localhost:5000/api/transactions", {
            credentials: "include"
        });

        const data = await response.json();

        if (!response.ok) {
            setError(data.error || "Failed to load transactions");
            return;
        }

        setTransactions(data);
        } catch (err) {
        console.error(err);
        setError("Network error");
        }
    };

    // Deleting transaction
    const deleteTransaction = async (id) => {
        if (!window.confirm("Delete this transaction?")) return;

        await fetch(`http://localhost:5000/api/transactions/${id}`, {
        method: "DELETE",
        credentials: "include"
        });

        fetchTransactions(); // reloading the table
    };

    // Returns to previous page
    const goBack = async () => {
        navigate("/planner")
    }

    // Handling Edits
    const handleEdit = (t) => {
        setEditingTransaction(t);
        setEditForm({
            type: t.type,
            category: t.category,
            spendingType: t.spendingType || "",
            amount: t.amount,
            date: t.date,
            description: t.description || ""
        });
        setEditError("");
    }

    // Saving Edits
    const saveEdit = async () => {
    // Validate
    if (!editForm.type || !editForm.category || !editForm.amount || !editForm.date) {
      setEditError("Type, Category, Amount, and Date are required");
      return;
    }

    try {
      const response = await fetch(`http://localhost:5000/api/transactions/${editingTransaction.id}`, {
        method: "PUT",
        credentials: "include",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(editForm)
      });

      const data = await response.json();
      if (!response.ok) {
        setEditError(data.error || "Failed to update transaction");
        return;
      }

      // Refresh table and close modal
      fetchTransactions();
      setEditingTransaction(null);
    } catch (err) {
      console.error(err);
      setEditError("Network error");
    }
  };

    // Sorting by Type
    const sortByType = () => {
        const direction = typeSortDir;
        const sorted = [...transactions].sort((a, b) => {
        if (a.type < b.type) return direction === "asc" ? -1 : 1;
        if (a.type > b.type) return direction === "asc" ? 1 : -1;
        return 0;
        });

        setTransactions(sorted);
        setTypeSortDir(direction === "asc" ? "desc" : "asc");
    }

    // Sorting by Category
    const sortByCategory = () => {
        const direction = categorySortDir;
        const sorted = [...transactions].sort((a, b) => {
        if (a.category < b.category) return direction === "asc" ? -1 : 1;
        if (a.category > b.category) return direction === "asc" ? 1 : -1;
        return 0;
        });

        setTransactions(sorted);
        setCategorySortDir(direction === "asc" ? "desc" : "asc");
    }

    // Sorting by Spending TYpe
    const sortBySpendingType = () => {
        const direction = spendingTypeSortDir;
        const sorted = [...transactions].sort((a, b) => {
        if (b.spendingType == "NULL") return -1;
        if (a.spendingType == "NULL") return 1;
        if (a.spendingType < b.spendingType) return direction === "asc" ? -1 : 1;
        if (a.spendingType > b.spendingType) return direction === "asc" ? 1 : -1;
        return 0;
        });

        setTransactions(sorted);
        setSpendingTypeSortDir(direction === "asc" ? "desc" : "asc");
    }

    // Sorting by Amount
    const sortByAmount = () => {
        const direction = amountSortDir;
        let aAmount = 0;
        let bAmount = 0;
        
        const sorted = [...transactions].sort((a, b) => {
            a.type === "expense" ? aAmount = -a.amount : aAmount = a.amount;
            b.type === "expense" ? bAmount = -b.amount : bAmount = b.amount;

            if (aAmount < bAmount) return direction === "asc" ? -1 : 1;
            if (aAmount > bAmount) return direction === "asc" ? 1 : -1;
            return 0;
        });

        setTransactions(sorted);
        setAmountSortDir(direction === "asc" ? "desc" : "asc");
    }

    // Parsing the Date
    const parseDate = (dateStr) => {
        const [day, month, year] = dateStr.split("/").map(Number);
        return new Date(year, month - 1, day); 
    };

    // Sorting by Date
    const sortByDate = () => {
        const direction = dateSortDir;

        const sorted = [...transactions].sort((a, b) => {
            const dateA = parseDate(a.date);
            const dateB = parseDate(b.date);

            if (dateA < dateB) return direction === "asc" ? -1 : 1;
            if (dateA > dateB) return direction === "asc" ? 1 : -1;
            return 0;
        })

        setTransactions(sorted);
        setDateSortDir(direction === "asc" ? "desc" : "asc");
    }

    return (
    <div style={{
      backgroundColor: "#dbeeff",
      border: "5px solid #003366",
      minHeight: "100vh",
      padding: "40px",
      fontFamily: "Arial"
    }}>
        
        {/* Title Container */}
        <div style={{
            display: "flex",
            alignItems: "center"
        }}>
            <button style={{
                ...buttonStyle,
                padding: "12px 20px",
                fontSize: "23px"}}
                 onClick={goBack}
            >
                Back
            </button>

            <h1 style={{ flex: 1, textAlign: "center" }}>
                TRANSACTIONS
            </h1>
        </div>
        
        {/* Error text */}
        {error && (
            <p style={{ color: "red", textAlign: "center" }}>{error}</p>
        )}

        {/* Transaction Table */}
        <table style={{
            width: "100%",
            borderCollapse: "collapse",
            marginTop: "30px",
            textAlign: "center"
        }}>
            {/* Column Headers */}
            <thead>
            <tr style={{ backgroundColor: "#003366", color: "white" }}>
                <th style={cellStyle}>
                    Type{" "}
                    <button
                        style={sortButtonStyle}
                        onClick={() => sortByType()}
                    >
                        ⇅
                    </button>
                </th>

                <th style={cellStyle}>
                    Category{" "}
                    <button
                        style={sortButtonStyle}
                        onClick={() => sortByCategory()}
                    >
                        ⇅
                    </button>
                </th>

                <th style={cellStyle}>
                    Spending Type{" "}
                    <button
                        style={sortButtonStyle}
                        onClick={() => sortBySpendingType()}
                    >
                        ⇅
                    </button>
                </th>

                <th style={cellStyle}>
                    Amount{" "}
                    <button
                        style={sortButtonStyle}
                        onClick={() => sortByAmount()}
                    >
                        ⇅
                    </button>
                </th>

                <th style={cellStyle}>
                    Date{" "}
                    <button
                        style={sortButtonStyle}
                        onClick={() => sortByDate()}
                    >
                        ⇅
                    </button>
                </th>

                <th style={cellStyle}>Description</th>
                <th style={cellStyle}>Actions</th>
            </tr>
            </thead>

            {/* Table Body */}
            <tbody>
            {transactions.length === 0 && (
                <tr>
                <td colSpan="7" style={{ textAlign: "center", padding: "20px" }}>
                    No transactions found
                </td>
                </tr>
            )}

            {transactions.map((t) => (
                <tr key={t.id}>

                <td style={cellStyle}>{t.type}</td>
                <td style={cellStyle}>{t.category}</td>
                <td style={cellStyle}>{t.spendingType !== "NULL" ? t.spendingType : "-"}</td>
                <td style={{
                    ...cellStyle,
                    color: t.type === "expense" ? "red" : "green",
                    fontWeight: "bold"
                }}>
                    {t.type === "expense" ? "-" : "+"}${t.amount.toFixed(2)}
                </td>
                <td style={cellStyle}>{t.date}</td>
                <td style={cellStyle}>{t.description}</td>

                <td style={cellStyle}>
                    <button
                    style={buttonStyle}
                    onClick={() => handleEdit(t)}
                    >
                    Edit
                    </button>

                    <button
                    style={{ ...buttonStyle, backgroundColor: "darkred" }}
                    onClick={() => deleteTransaction(t.id)}
                    >
                    Delete
                    </button>
                </td>

                </tr>
            ))}
            </tbody>
        </table>

        {/* Edit Modal */}
        {editingTransaction && (
            <div style={modalOverlayStyle}>
                <div style={modalContentStyle}>
                <h2>Edit Transaction</h2>

                {/* Type */}
                <label>Type:</label>
                <select
                    value={editForm.type}
                    onChange={(e) => setEditForm({ ...editForm, type: e.target.value })}
                >
                    <option value="income">Income</option>
                    <option value="expense">Expense</option>
                </select>

                {/* Category Dropdown */}
                <label>Category:</label>
                <select
                    value={editForm.category}
                    onChange={(e) => setEditForm({ ...editForm, category: e.target.value })}
                >
                    <option value="">--Select Category--</option>
                    <option value="Salary">Salary</option>
                    <option value="Groceries">Groceries</option>
                    <option value="Entertainment">Entertainment</option>
                    <option value="Utilities">Utilities</option>
                    <option value="Other">Other</option>
                </select>

                {/* Amount */}
                <label>Amount:</label>
                <input
                    type="text"
                    value={editForm.amount}
                    onChange={(e) => setEditForm({ ...editForm, amount: e.target.value })}
                />

                {/* Date */}
                <label>Date:</label>
                <input
                    type="text"
                    value={editForm.date}
                    onChange={(e) => setEditForm({ ...editForm, date: e.target.value })}
                    placeholder="DD/MM/YYYY"
                />

                {/* Description (multi-line) */}
                <label>Description:</label>
                <textarea
                    rows={4}
                    value={editForm.description}
                    onChange={(e) => setEditForm({ ...editForm, description: e.target.value })}
                />

                {/* Error message */}
                {editError && <p style={{ color: "red" }}>{editError}</p>}

                {/* Buttons */}
                <div style={{ marginTop: "10px" }}>
                    <button
                    style={{ ...buttonStyle, marginRight: "10px" }}
                    onClick={() => saveEdit()}
                    >
                    Save
                    </button>
                    <button style={buttonStyle} onClick={() => setEditingTransaction(null)}>
                    Cancel
                    </button>
                </div>
                </div>
            </div>
        )}

    </div>
  );
}

const buttonStyle = {
  padding: "6px 10px",
  margin: "3px",
  backgroundColor: "#003366",
  color: "white",
  border: "none",
  borderRadius: "4px",
  cursor: "pointer"
};

const cellStyle = {
  border: "2px solid black",
  padding: "8px"
};

const sortButtonStyle = {
  marginLeft: "5px",
  padding: "2px 5px",
  fontSize: "12px",
  cursor: "pointer",
  borderRadius: "4px",
  border: "1px solid #ccc",
  backgroundColor: "#f0f0f0"
};


// Modals for editing
const modalOverlayStyle = {
  position: "fixed",
  top: 0, left: 0, right: 0, bottom: 0,
  backgroundColor: "rgba(0,0,0,0.5)",
  display: "flex",
  justifyContent: "center",
  alignItems: "center",
  zIndex: 1000
};

const modalContentStyle = {
  backgroundColor: "#fff",
  padding: "20px",
  borderRadius: "8px",
  width: "400px",
  display: "flex",
  flexDirection: "column",
  gap: "10px"
};

export default Transactions;
