import {
    SORT_DIRECTION_ASC,
    SORT_DIRECTION_DESC,
    SORT_GREATER,
    SORT_LESS
} from "./constants.js";

// Generic helper to sort data
export const sortTransactions = (keyFn, direction, setDirection) => {
    const sorted = [...transactions].sort((a, b) => {
        const aVal = keyFn(a), bVal = keyFn(b);
        if (aVal < bVal) return direction === SORT_DIRECTION_ASC ? SORT_LESS : SORT_GREATER;
        if (aVal > bVal) return direction === SORT_DIRECTION_ASC ? SORT_GREATER : SORT_LESS;
        return 0;
    });
    setTransactions(sorted);
    setDirection(direction === SORT_DIRECTION_ASC ? SORT_DIRECTION_DESC : SORT_DIRECTION_ASC);
};
