import React, { useState, useEffect } from "react";
import usePlannerData from "../hooks/usePlannerData";
import { useNavigate, Link } from "react-router-dom";

// Views
import GeneralView from "../components/planner/GeneralView";
import IncomeView from "../components/planner/IncomeView";
import ExpenseView from "../components/planner//ExpenseView";
import TrendsView from "../components/planner/TrendsView";

// Shared components
import ViewSelector from "../components/planner/ViewSelector";

function Planner() {
  const navigate = useNavigate();

  // States
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [typeOption, setTypeOption] = useState("");
  const [categoryOption, setCategoryOption] = useState("");
  const [amountValue, setAmountValue] = useState("");
  const [dateValue, setDateValue] = useState("");
  const [descriptionValue, setDescriptionValue] = useState("");

  // View Date Range
  const [yearValue, setYearValue] = useState("2026");
  const [prevYearValue, setPrevYearValue] = useState("2026");
  const [monthValue, setMonthValue] = useState("01");
  const [dateRangeOption, setDateRangeOption] = useState("monthly");

  // View States
  const [currentView, setCurrentView] = useState("General");

  // Formatting the date
  const formattedDate = `${monthValue}/${yearValue}`;
  const prevFormattedDate = `${monthValue}/${prevYearValue}`;

  // Defining variables for use
  const {
    totalIncome,
    totalExpenses,
    prevIncome,
    prevExpenses,
    totalSavings,
    categoryIncome,
    categoryExpenses,
    spendingType: categorySpendingType,
    transactions: periodTransactions,
    largestIncome,
    largestExpense,
    fastestGrowingIncome: changingIncome,
    fastestGrowingExpenses: changingExpenses,
    categoryGrowth,
    loading,
    error: fetchError,
    refetch
  } = usePlannerData(formattedDate, prevFormattedDate);

  // -- CALCULATING STATS--
  const emergencyFund = totalExpenses * 6;

  const incomeChange = totalIncome - prevIncome;
  const incomeChangePercent =
    prevIncome !== 0 ? ((incomeChange / prevIncome) * 100).toFixed(0) : 0;

  const expenseChange = totalExpenses - prevExpenses;
  const expenseChangePercent =
    prevExpenses !== 0 ? ((expenseChange / prevExpenses) * 100).toFixed(0) : 0;

  const periodCashflow = periodTransactions.map(item => ({
    period: item.period,
    net: item.income - item.expense
  }));

  const periodIncome = periodTransactions.map(item => ({
    period: item.period,
    income: item.income
  }));

  const periodExpenses = periodTransactions.map(item => ({
    period: item.period,
    expense: item.expense
  }));

  const savingsRate = totalIncome > 0 ? (((totalIncome - totalExpenses) / totalIncome) * 100).toFixed(2) : 0;
  const EIRatio = totalIncome > 0 ? (totalExpenses / totalIncome).toFixed(2) : 0;
  const runway = totalExpenses > 0 ? Math.floor(totalSavings / totalExpenses) : 0;

  // Categories mapped by type
  const categories = {
    income: ["Salary", "Interest", "Investments", "Bonuses", "Other Income"],
    expense: ["Rent", "Utilities", "Insurance", "Health/Medical", "Education", "Travel", "Subscriptions", "Loan payment", "Leisure", "Clothes", "Food", "Other Expense"],
    savings: ["Deposit", "Withdrawal"]
  };

  // Discretionary vs non discretionary mapping
  const spendingType = {
    discretionary: ["Travel", "Leisure", "Clothes", "Other Expense"],
    nonDiscretionary: ["Rent", "Utilities", "Insurance", "Health/Medical", "Education", "Subscriptions", "Loan payment", "Savings", "Food"]
  }

  // Updating the view 
  const handleViewChange = (view) => setCurrentView(view);

  const handleTransactionAdd = async () => {
    setError("");
    setSuccess("");

    // Transaction type is not blank
    if (!typeOption || !categoryOption || !amountValue || !dateValue) {
      setError("Required fields are empty");
      return;
    }

    // Amount is within bounds > 0 and < 1000000000 and if it has a $ or . it is removed and accounted for
    const amount = parseFloat(amountValue.replace(/[$,]/g, ""));
    if (isNaN(amount) || amount <= 0 || amount >= 1000000000) {
      setError("Invalid amount. Must be between $0 and $1 billion.");
      return;
    }

    // Date has correct format and is valid
    const dateRegex = /^(0?[1-9]|[12][0-9]|3[01])\/(0?[1-9]|1[0-2])\/\d{4}$/;
    if (!dateRegex.test(dateValue)) {
      setError("Invalid date.");
      return;
    }

    // Year is within reasonable range
    const year = parseInt(dateValue.split("/")[2], 10);

    if (year < 1976 || year > 2076) {
      setError("Year must be between 1976 and 2076.");
      return;
    }

    // Assigning spending type
    let spending = "NULL";
    if (spendingType.discretionary.includes(categoryOption)) {
      spending = "Discretionary";
    } else if (spendingType.nonDiscretionary.includes(categoryOption)) {
      spending = "Non-Discretionary";
    }

    // Storing the data
    try {
      const response = await fetch("http://localhost:5000/api/add", { 
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({ 
          type: typeOption, 
          category: categoryOption, 
          spendingType: spending,
          amount: amount, 
          date: dateValue, 
          description:descriptionValue 
        }),
      });

      const data = await response.json();

      if (!response.ok) {
        alert(data.message || "Adding transaction failed");
        return;
      }

      setSuccess("Added Transaction!");

      // Resetting states
      setTypeOption("");
      setCategoryOption("");
      setAmountValue("");
      setDateValue("");
      setDescriptionValue("");

      // Refetching data
      refetch();
    } catch (error) {
      console.error("Error adding transaction:", error);
      setError("Network error.");
    }
  };

  // Going to the transactions page
  const handleTransactionEdit = async () => {
    navigate("/transactions")
  }

  // --- Render Selected View ---
  const renderView = () => {
    switch (currentView) {
      case "Income":
        return <IncomeView
          totalIncome={totalIncome}
          incomeChange={incomeChange}
          incomeChangePercent={incomeChangePercent}
          largestIncome={largestIncome}
          periodIncome={periodIncome}
          categoryIncome={categoryIncome}
        />;
      case "Expense":
        return <ExpenseView
          totalExpenses={totalExpenses}
          expenseChange={expenseChange}
          expenseChangePercent={expenseChangePercent}
          largestExpense={largestExpense}
          periodExpenses={periodExpenses}
          categoryExpenses={categoryExpenses}
          categorySpendingType={categorySpendingType}
        />;
      case "Trends":
        return <TrendsView
          changingIncome={changingIncome}
          changingExpenses={changingExpenses}
          categoryGrowth={categoryGrowth}
        />;
      default: // General
        return <GeneralView
          totalIncome={totalIncome}
          totalExpenses={totalExpenses}
          totalSavings={totalSavings}
          periodTransactions={periodTransactions}
          savingsRate={savingsRate}
          EIRatio={EIRatio}
          emergencyFund={emergencyFund}
          runway={runway}
        />;
    }
  };

  return (
    <div style={{ display: "flex", minHeight: "100vh" }}>
      {/* Left Panel */}
      <div style={{ width: "300px", backgroundColor: "#003366", padding: "20px", color: "white", display: "flex", flexDirection: "column", alignItems: "center" }}>
        <h2>ENTER A TRANSACTION</h2>
       {/* View Selector */}
        <ViewSelector currentView={currentView} handleViewChange={handleViewChange} />
      </div>

      {/* Main Content */}
      <div style={{ flex: 1, padding: "20px", backgroundColor: "#ffffff" }}>
        {renderView()}
      </div>
    </div>
  );
}

export default Planner;
