import React, { useState } from "react";

function Planner() {
  // States
  const [amountValue, setAmountValue] = useState("");
  const [dateValue, setDateValue] = useState("");
  const [typeOption, setTypeOption] = useState("");
  const [categoryOption, setCategoryOption] = useState("");

  // Categories mapped by type
  const categories = {
    income: ["Salary", "Interest", "Investments", "Bonuses", "Other Income"],
    expense: [
      "Rent",
      "Utilities",
      "Insurance",
      "Health/Medical",
      "Education",
      "Travel",
      "Subscriptions",
      "Loan payment",
      "Savings",
      "Leisure",
      "Clothes",
      "Food",
      "Other Expense"
    ]
  };

  return (
    <div style={{
      display: "flex",
      minHeight: "100vh",
      backgroundColor: "#dbeeff",
      fontFamily: "Arial, sans-serif"
    }}>

      {/* Left Ribbon */}
      <div style={{
        width: "300px",
        backgroundColor: "#003366",
        padding: "20px",
        color: "white",
        display: "flex",
        flexDirection: "column",
        alignItems: "center"
      }}>
        <h2>ENTER A TRANSACTION</h2>

        {/* Transaction Type Dropdown */}
        <select
          value={typeOption}
          onChange={(e) => {
            setTypeOption(e.target.value);
            setCategoryOption(""); // reset category when type changes
          }}
          style={{
            padding: "10px",
            fontSize: "16px",
            borderRadius: "6px",
            border: "none",
            width: "100%",
            marginBottom: "20px"
          }}
        >
          <option value="">Transaction Type</option>
          <option value="income">Income</option>
          <option value="expense">Expense</option>
        </select>

        {/* Amount */}
        <input
          type="text"
          placeholder="Amount"
          value={amountValue}
          onChange={(e) => setAmountValue(e.target.value)}
          style={{
            padding: "10px",
            fontSize: "16px",
            borderRadius: "6px",
            border: "none",
            width: "94%",
            marginBottom: "20px"
          }}
        />

        {/* Category Dropdown */}
        <select
          value={categoryOption}
          onChange={(e) => setCategoryOption(e.target.value)}
          disabled={!typeOption} // disabled until type is selected
          style={{
            padding: "10px",
            fontSize: "16px",
            borderRadius: "6px",
            border: "none",
            width: "100%",
            marginBottom: "20px"
          }}
        >
          <option value="">Category</option>
          {typeOption && categories[typeOption].map((cat, index) => (
            <option key={index} value={cat}>{cat}</option>
          ))}
        </select>

        {/* Date */}
        <input
          type="text"
          placeholder="date"
          value={dateValue}
          onChange={(e) => setDateValue(e.target.value)}
          style={{
            padding: "10px",
            fontSize: "16px",
            borderRadius: "6px",
            border: "none",
            width: "94%",
            marginBottom: "20px"
          }}
        />
      </div>

      {/* Main Content Area */}
      <div style={{
        flex: 1,
        padding: "40px",
        textAlign: "center"
      }}>
        <h1>Financial Planner</h1>
        <p>Analytics and charts will go here.</p>
        {typeOption && <p>Transaction Type: {typeOption}</p>}
        {categoryOption && <p>Category: {categoryOption}</p>}
        {inputValue && <p>Amount: {inputValue}</p>}
      </div>

    </div>
  );
}

export default Planner;
